import io.netty.bootstrap.Bootstrap;
import io.netty.buffer.ByteBuf;
import io.netty.buffer.Unpooled;
import io.netty.channel.Channel;
import io.netty.channel.ChannelFuture;
import io.netty.channel.ChannelHandlerContext;
import io.netty.channel.ChannelInitializer;
import io.netty.channel.ChannelOption;
import io.netty.channel.EventLoopGroup;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.InsecureTrustManagerFactory;

import java.nio.charset.StandardCharsets;

public class FactorialClient {
    private boolean SSL;
    private String HOST = "127.0.0.1";
    private int PORT = 8322;
    private int COUNT = 1000;
    private final FactorialClientHandler handler;

    public FactorialClient(boolean ssl) {
        this.SSL = ssl;
        this.handler = new FactorialClientHandler(COUNT);
    }

    public void start() throws Exception {
        EventLoopGroup group = new NioEventLoopGroup();
        try {
            Bootstrap bootstrap = new Bootstrap();
            bootstrap.group(group)
                    .channel(NioSocketChannel.class)
                    .option(ChannelOption.SO_KEEPALIVE, true)
                    .handler(new ChannelInitializer<SocketChannel>() {
                        @Override
                        protected void initChannel(SocketChannel ch) throws Exception {
                            ChannelPipeline pipeline = ch.pipeline();
                            if (SSL) {
                                SslContext sslCtx = SslContextBuilder.forClient()
                                        .trustManager(InsecureTrustManagerFactory.INSTANCE)
                                        .build();
                                pipeline.addLast(sslCtx.newHandler(ch.alloc(), HOST, PORT));
                            }
                            pipeline.addLast(handler);
                        }
                    });

            ChannelFuture future = bootstrap.connect(HOST, PORT).sync();
            future.channel().closeFuture().sync();
            System.err.println(handler.getResult());
        } finally {
            group.shutdownGracefully();
        }
    }

    private static class FactorialClientHandler extends ChannelHandlerAdapter {
        private final int count;
        private final ByteBuf response = Unpooled.buffer();
        private String result;

        public FactorialClientHandler(int count) {
            this.count = count;
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            ByteBuf buffer = Unpooled.buffer();
            buffer.writeBytes(String.valueOf(count).getBytes(StandardCharsets.UTF_8));
            buffer.writeByte('\n');
            ctx.writeAndFlush(buffer);
        }

        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            ByteBuf in = (ByteBuf) msg;
            try {
                while (in.isReadable()) {
                    byte b = in.readByte();
                    if (b == '\n') {
                        break;
                    }
                    response.writeByte(b);
                }
            } finally {
                in.release();
            }
        }

        @Override
        public void channelReadComplete(ChannelHandlerContext ctx) {
            result = response.toString(StandardCharsets.UTF_8);
            ctx.close();
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }

        public String getResult() {
            return result;
        }
    }

    public static void main(String[] args) throws Exception {
        new FactorialClient(false).start();
    }
}
