        @Override
        protected void encode(ChannelHandlerContext ctx, XxlRpcRequest msg, List<Object> out) throws Exception {
            byte[] data = serializer.serialize(msg);
            ByteBuf buf = ctx.alloc().buffer(4 + data.length);
