        bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast(new RpcRequestEncoder());
                        pipeline.addLast(new RpcResponseDecoder());
                        pipeline.addLast(new RpcClientHandler(unprocessedRequests));
                    }
                });
    }

    private Channel doConnect(InetSocketAddress inetSocketAddress) {
        try {
            ChannelFuture future = bootstrap.connect(inetSocketAddress).sync();
            return future.channel();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            throw new RuntimeException("Interrupted while connecting", e);
        }
    }

    @Override
    public CompletableFuture<Object> sendRpcRequest(RpcRequest rpcRequest) {
        InetSocketAddress address = serviceDiscovery.lookupService(rpcRequest.getServiceName());
        Channel channel = getChannel(address);
        CompletableFuture<Object> future = new CompletableFuture<>();
        unprocessedRequests.add(rpcRequest.getRequestId(), future);
        channel.writeAndFlush(rpcRequest).addListener((ChannelFutureListener) future1 -> {
            if (!future1.isSuccess()) {
                future.completeExceptionally(future1.cause());
            }
        });
        return future;
    }

    private Channel getChannel(InetSocketAddress inetSocketAddress) {
        Channel channel = channelProvider.get(inetSocketAddress);
        if (channel != null && channel.isActive()) {
            return channel;
        }
        channel = doConnect(inetSocketAddress);
        channelProvider.add(inetSocketAddress, channel);
        return channel;
    }

    @Override
    public void close() {
        eventLoopGroup.shutdownGracefully();
        channelProvider.closeAll();
    }

    private class RpcClientHandler extends SimpleChannelInboundHandler<RpcResponse> {
        private final UnprocessedRequests unprocessedRequests;

        public RpcClientHandler(UnprocessedRequests unprocessedRequests) {
            this.unprocessedRequests = unprocessedRequests;
        }

