public final class EchoClient {

    private final String hostName;
    private final int portNumber;
    private EventLoopGroup group;
    private Channel channel;

    public EchoClient(String hostName, int portNumber) {
        this.hostName = hostName;
        this.portNumber = portNumber;
    }

    public void connect() throws InterruptedException {
        group = new NioEventLoopGroup();
        Bootstrap b = new Bootstrap();
        b.group(group)
         .channel(NioSocketChannel.class)
         .option(ChannelOption.TCP_NODELAY, true)
         .handler(new EchoClientHandler());

        ChannelFuture f = b.connect(hostName, portNumber).sync();
        channel = f.channel();
    }

    public void sendMessage(String message) {
        ByteBuffer writeBuffer = ByteBuffer.allocate(32);
        writeBuffer.put(message.getBytes());
        writeBuffer.flip();
        writeBuffer.rewind();

        ByteBuf buf = Unpooled.copiedBuffer(writeBuffer);
        channel.writeAndFlush(buf);
        writeBuffer.clear();
    }

    public void startConsoleInput() throws IOException {
        try (BufferedReader stdIn = new BufferedReader(new InputStreamReader(System.in))) {
            String userInput;
            while ((userInput = stdIn.readLine()) != null) {
                sendMessage(userInput);
            }
        } catch (UnknownHostException e) {
            System.err.println("不明主机，主机名为： " + hostName);
            System.exit(1);
        } catch (IOException e) {
            System.err.println("不能从主机中获取I/O，主机名为：" + hostName);
            System.exit(1);
        }
    }

    public void shutdown() {
        if (group != null) {
            group.shutdownGracefully();
        }
    }
