import io.netty.bootstrap.Bootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.ssl.SslHandler;
import javax.net.ssl.SSLContext;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import java.util.concurrent.TimeUnit;

public class ProxyClientContainer extends ChannelInboundHandlerAdapter {
    private static final Logger logger = LoggerFactory.getLogger(ProxyClientContainer.class);
    private final NioEventLoopGroup workerGroup = new NioEventLoopGroup();
    private final Bootstrap bootstrap = new Bootstrap();
    private final Bootstrap realServerBootstrap = new Bootstrap();
    private final Config config = Config.getInstance();
    private final SSLContext sslContext;
    private long sleepTimeMill = 1000;

    public ProxyClientContainer() {
        sslContext = configureSslContext();
        initBootstrap();
    }

    private void initBootstrap() {
        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (config.isSslEnabled()) {
                            pipeline.addLast(createSslHandler(sslContext));
                        }
                        pipeline.addLast(ProxyClientContainer.this);
                    }
                });
    }

    private SSLContext configureSslContext() {
        if (config.isSslEnabled()) {
            try {
                SSLContext context = SSLContext.getInstance("TLS");
                context.init(null, null, null);
                return context;
            } catch (Exception e) {
                logger.error("Failed to create SSL context", e);
                throw new RuntimeException(e);
            }
        }
        return null;
    }

    private ChannelHandler createSslHandler(SSLContext sslContext) {
        return new SslHandler(sslContext.createSSLEngine()) {
            {
                engine.setUseClientMode(true);
            }
        };
    }

    public void start() {
        connectProxyServer();
    }

    private void connectProxyServer() {
        bootstrap.connect(config.getProxyHost(), config.getProxyPort()).addListener((ChannelFutureListener) future -> {
            if (!future.isSuccess()) {
                logger.warn("Connection failed, retrying in {} ms", sleepTimeMill);
                reconnectWait();
                workerGroup.next().schedule(this::connectProxyServer, sleepTimeMill, TimeUnit.MILLISECONDS);
            } else {
                logger.info("Connected to proxy server");
            }
        });
    }

    private void reconnectWait() {
        sleepTimeMill = Math.min(sleepTimeMill * 2, config.getMaxReconnectDelay());
    }

    @Override
    public void channelInactive(ChannelHandlerContext ctx) {
        logger.info("Connection lost, reconnecting...");
        reconnectWait();
        ctx.executor().schedule(() -> connectProxyServer(), sleepTimeMill, TimeUnit.MILLISECONDS);
    }

    public void stop() {
        workerGroup.shutdownGracefully();
    }
}

