import javax.annotation.PostConstruct;
import javax.annotation.PreDestroy;
import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Value;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import io.netty.bootstrap.Bootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.codec.serialization.*;

@Component
public class NettyClient {

    private static final int RECONNECT_SECONDS = 5;
    private static final Logger logger = LoggerFactory.getLogger(NettyClient.class);

    @Value("${server.host}")
    private String serverHost;

    @Value("${server.port}")
    private int serverPort;

    private EventLoopGroup eventGroup;
    private Bootstrap bootstrap;
    private volatile Channel channel;

    @PostConstruct
    public void start() {
        eventGroup = new NioEventLoopGroup();
        bootstrap = new Bootstrap();
        bootstrap.group(eventGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) throws Exception {
                        ch.pipeline().addLast(new ObjectEncoder());
                        ch.pipeline().addLast(new ObjectDecoder(ClassResolvers.cacheClassLoadingResolver()));
                    }
                });
        doConnect();
    }

    private void doConnect() {
        bootstrap.connect(serverHost, serverPort).addListener((ChannelFutureListener) future -> {
            if (future.isSuccess()) {
                logger.info("Connected to server at {}:{}, channel: {}", serverHost, serverPort, future.channel());
                Channel newChannel = future.channel();
                newChannel.closeFuture().addListener(f -> {
                    logger.info("Channel closed, scheduling reconnection.");
                    reconnect();
                });
                channel = newChannel;
            } else {
                logger.error("Failed to connect to server, trying to reconnect...", future.cause());
                reconnect();
            }
        });
    }

    private void reconnect() {
        eventGroup.schedule(this::doConnect, RECONNECT_SECONDS, java.util.concurrent.TimeUnit.SECONDS);
    }

    @PreDestroy
    public void shutdown() {
        if (channel != null) {
            channel.close();
        }
        if (eventGroup != null) {
            eventGroup.shutdownGracefully();
        }
    }

    public void send(Invocation invocation) {
        if (channel != null && channel.isActive()) {
            channel.writeAndFlush(invocation);
        } else {
            logger.warn("Channel is not active, cannot send message.");
        }
    }
}
