import io.netty.bootstrap.Bootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.codec.http.*;
import io.netty.handler.codec.http.HttpMethod;
import io.netty.handler.codec.http.HttpVersion;
import io.netty.buffer.Unpooled;
import io.netty.handler.codec.http.HttpHeaderNames;
import io.netty.handler.codec.http.HttpObjectAggregator;
import java.net.URI;

public class NettyHttpConnectClient {
    private EventLoopGroup group;
    private Channel channel;
    private Serializer serializer;
    private String address;
    private String host;

    public void init(String address, Serializer serializer, XxlRpcInvokerFactory xxlRpcInvokerFactory) throws Exception {
        this.address = address;
        this.serializer = serializer;
        URI uri = new URI(address);
        this.host = uri.getHost();
        int port = uri.getPort();
        group = new NioEventLoopGroup();
        Bootstrap bootstrap = new Bootstrap();
        bootstrap.group(group)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast("codec", new HttpClientCodec());
                        pipeline.addLast("aggregator", new HttpObjectAggregator(65536));
                        pipeline.addLast("handler", new ResponseHandler(serializer, xxlRpcInvokerFactory));
                    }
                });
        ChannelFuture future = bootstrap.connect(host, port).sync();
        channel = future.channel();
    }

    public boolean isValidate() {
        return channel != null && channel.isActive();
    }

    public void close() {
        if (channel != null) {
            channel.close();
        }
        if (group != null) {
            group.shutdownGracefully();
        }
    }

    public void send(XxlRpcRequest xxlRpcRequest) {
        if (!isValidate()) {
            throw new IllegalStateException("Connection is not valid");
        }
        byte[] serialized = serializer.serialize(xxlRpcRequest);
        FullHttpRequest request = new DefaultFullHttpRequest(
                HttpVersion.HTTP_1_1,
                HttpMethod.POST,
                "/invoke",
                Unpooled.wrappedBuffer(serialized)
        );
        request.headers().set(HttpHeaderNames.HOST, host);
        request.headers().set(HttpHeaderNames.CONTENT_TYPE, "application/json");
        request.headers().set(HttpHeaderNames.CONTENT_LENGTH, request.content().readableBytes());
        channel.writeAndFlush(request);
    }

    private static class ResponseHandler extends ChannelInboundHandlerAdapter {
        private final Serializer serializer;
        private final XxlRpcInvokerFactory invokerFactory;

        ResponseHandler(Serializer serializer, XxlRpcInvokerFactory invokerFactory) {
            this.serializer = serializer;
            this.invokerFactory = invokerFactory;
        }

        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            if (msg instanceof FullHttpResponse) {
                FullHttpResponse response = (FullHttpResponse) msg;
                try {
                    ByteBuf content = response.content();
                    byte[] bytes = new byte[content.readableBytes()];
                    content.getBytes(0, bytes);
                    XxlRpcResponse rpcResponse = serializer.deserialize(bytes, XxlRpcResponse.class);
                    invokerFactory.handleResponse(rpcResponse);
                } finally {
                    ReferenceCountUtil.release(msg);
                }
            }
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }
    }
}

