    private final com.codahale.metrics.Counter connectionSuccess;
    private final com.codahale.metrics.Counter connectionFailure;
    private final com.codahale.metrics.Gauge<Double> liveConnections;
    private final com.codahale.metrics.Gauge<Double> nettyChannelBufferSize;
    private final int lowWriteBufferWatermark;
    private final int highWriteBufferWatermark;
    private final EventLoopGroup eventLoopGroup;
    private final EventLoopGroup encoderEventLoopGroup;
    private final Bootstrap bootstrap;
    private final ConcurrentMap<String, Channel> channels = new ConcurrentHashMap<>();

    public HttpEventChannelManager(com.codahale.metrics.MetricRegistry registry, MrePublishConfiguration config) {
        connectionSuccess = registry.counter("http-event-channel.connection-success");
        connectionFailure = registry.counter("http-event-channel.connection-failure");
        liveConnections = () -> (double) channels.size();
        registry.register("http-event-channel.live-connections", liveConnections);
        nettyChannelBufferSize = () -> 0.0;
        registry.register("http-event-channel.buffer-size", nettyChannelBufferSize);
        lowWriteBufferWatermark = config.getLowWriteBufferWatermark();
        highWriteBufferWatermark = config.getHighWriteBufferWatermark();

        eventLoopGroup = new NioEventLoopGroup();
        encoderEventLoopGroup = new NioEventLoopGroup();

        bootstrap = new Bootstrap();
        bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.WRITE_BUFFER_LOW_WATER_MARK, lowWriteBufferWatermark)
                .option(ChannelOption.WRITE_BUFFER_HIGH_WATER_MARK, highWriteBufferWatermark)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) {
                        ch.pipeline().addLast(new IdleStateHandler(0, 0, 60));
                        ch.pipeline().addLast(new ChannelInboundHandlerAdapter() {
                            @Override
                            public void channelWritabilityChanged(ChannelHandlerContext ctx) {
                                nettyChannelBufferSize(() -> (double) ctx.channel().bytesBeforeUnwritable());
                            }
                        });
                        ch.pipeline().addLast(new ChannelOutboundHandlerAdapter() {
                            @Override
                            public void write(ChannelHandlerContext ctx, Object msg, ChannelPromise promise) {
                                super.write(ctx, msg, promise);
                            }
                        });
                    }
                });
    }

    public Channel findOrCreate(InetSocketAddress address) {
        String key = getHostPortString(address);
        return channels.computeIfAbsent(key, k -> {
            try {
                ChannelFuture future = bootstrap.connect(address).sync();
                Channel channel = future.channel();
                channel.closeFuture().addListener(f -> {
                    channels.remove(k);
                });
                if (future.isSuccess()) {
                    connectionSuccess.inc();
                } else {
                    connectionFailure.inc();
                    throw new RuntimeException("Connection failed", future.cause());
                }
                return channel;
            } catch (Exception e) {
                connectionFailure.inc();
                throw new RuntimeException(e);
            }
        });
    }

    public Channel find(InetSocketAddress address) {
        return channels.get(getHostPortString(address));
    }

    public void close(InetSocketAddress address) {
        String key = getHostPortString(address);
        Channel channel = channels.remove(key);
        if (channel != null) {
            channel.close();
        }
    }

    private String getHostPortString(InetSocketAddress address) {
        return address.getHostString() + ":" + address.getPort();
    }

    public void shutdown() {
        for (Channel channel : channels.values()) {
            channel.close();
        }
        eventLoopGroup.shutdownGracefully();
        encoderEventLoopGroup.shutdownGracefully();
    }
}

