import java.util.concurrent.TimeUnit;

public abstract class TrackerClient {
    private final boolean secure;
    private final long interval;
    private final Bootstrap bootstrap;
    private final int port;
    private final String address;
    private final String[] devices;
    private final ChannelGroup channelGroup;
    private final NioEventLoopGroup group = new NioEventLoopGroup();
    private final ScheduledExecutorService reconnectScheduler = Executors.newSingleThreadScheduledExecutor();
    private volatile boolean running = false;
    private ScheduledFuture<?> reconnectTask;

    public TrackerClient(Config config, String protocol) {
        this.secure = config.isSecure();
        this.interval = config.getInterval();
        this.port = config.getPort();
        this.address = config.getAddress();
        this.devices = config.getDevices();
        this.bootstrap = new Bootstrap();
        this.channelGroup = new DefaultChannelGroup(GlobalEventExecutor.INSTANCE);
        this.bootstrap.group(group)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<NioSocketChannel>() {
                    @Override
                    protected void initChannel(NioSocketChannel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (secure) {
                            try {
                                SslContext sslContext = config.getSslContext();
                                SSLEngine engine = sslContext.newEngine(ch.alloc(), address, port);
                                engine.setUseClientMode(true);
                                pipeline.addLast("ssl", new SslHandler(engine));
                            } catch (Exception e) {
                                throw new RuntimeException("Failed to initialize SSL", e);
                            }
                        }
                        addProtocolHandlers(pipeline, config);
                        pipeline.addLast("reconnect-handler", new ChannelInboundHandlerAdapter() {
                            @Override
                            public void channelInactive(ChannelHandlerContext ctx) {
                                if (running && interval > 0) {
                                    scheduleReconnect();
                                }
                            }
                        });
                    }
                });
    }

    public abstract void addProtocolHandlers(ChannelPipeline pipeline, Config config);

    public void start() {
        running = true;
        connect();
    }

    private void connect() {
        ChannelFuture future = bootstrap.connect(new InetSocketAddress(address, port));
        future.addListener((ChannelFutureListener) f -> {
            if (f.isSuccess()) {
                channelGroup.add(f.channel());
                f.channel().closeFuture().addListener((ChannelFutureListener) future1 -> {
                    if (running && interval > 0) {
                        scheduleReconnect();
                    }
                });
            } else {
                scheduleReconnect();
            }
        });
    }

    private void scheduleReconnect() {
        if (interval <= 0 || !running) {
            return;
        }
        if (reconnectTask != null) {
            reconnectTask.cancel(false);
        }
        long jitter = (long) (interval * 0.1 * ThreadLocalRandom.current().nextDouble());
        reconnectTask = reconnectScheduler.schedule(this::connect, interval + jitter, TimeUnit.SECONDS);
    }

    public void stop() {
        running = false;
        if (reconnectTask != null) {
            reconnectTask.cancel(false);
        }
        channelGroup.close().awaitUninterruptibly();
        group.shutdownGracefully();
        reconnectScheduler.shutdownNow();
    }

    public boolean isDatagram() {
        return false;
    }

    public boolean isSecure() {
        return secure;
    }

    public String[] getDevices() {
        return devices;
    }

    public ChannelGroup getChannelGroup() {
        return channelGroup;
    }
}

