    public NettyHttp2Client(String host, int port) {
        this.workerGroup = new NioEventLoopGroup();
        this.initializer = new Http2ClientInitializer(Integer.MAX_VALUE);
        this.bootstrap = new Bootstrap();
        configureBootstrap(host, port);
    }

    private void configureBootstrap(String host, int port) {
        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.SO_KEEPALIVE, true)
                .remoteAddress(host, port)
                .handler(initializer);
    }

