        this.initializer = new Http2ClientInitializer();
        configureBootstrap();
    }

    private void configureBootstrap() {
        bootstrap = new Bootstrap();
        bootstrap.group(workerGroup)
                .channel(NioSocketChannel.class)
                .handler(initializer);
    }

    public void connect() {
        try {
            channel = bootstrap.connect(host, port).sync().channel();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    public void sendRequest(URI hostName) {
        String path = hostName.getRawPath();
        DefaultHttp2Headers headers = new DefaultHttp2Headers();
        headers.method(HttpMethod.GET.name());
        headers.scheme("https");
        headers.authority(this.host + ":" + this.port);
        headers.path(path);

        channel.writeAndFlush(headers);
    }

    public void disconnect() {
        channel.close();
        workerGroup.shutdownGracefully();
    }

