import java.util.logging.Logger;
import java.util.concurrent.TimeUnit;
import org.slf4j.LoggerFactory;

public class NettyTcpClient {
    private static final Logger LOG = LoggerFactory.getLogger(NettyTcpClient.class);
    private final URI uri;
    private Channel webSocketChannel;
    private final EventLoopGroup eventLoopGroup = new NioEventLoopGroup();
    private boolean compressedMessages;
    private boolean acceptAllCertificates;
    private boolean enableLoggingHandler;
    private LogLevel loggingHandlerLevel;
    private String socksProxyHost;
    private Integer socksProxyPort;
    private WebSocketMessageHandler messageHandler;

    public NettyTcpClient(URI uri) {
        this.uri = uri;
    }

    public void connect() {
        try {
            Bootstrap bootstrap = new Bootstrap();
            bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .handler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();

                        if (socksProxyHost != null && socksProxyPort != null) {
                            pipeline.addLast(new Socks5ProxyHandler(
                                new InetSocketAddress(socksProxyHost, socksProxyPort)));
                        }

                        boolean isSecure = "wss".equals(uri.getScheme());
                        if (isSecure) {
                            SslContext sslCtx;
                            if (acceptAllCertificates) {
                                sslCtx = SslContextBuilder.forClient()
                                    .trustManager(InsecureTrustManagerFactory.INSTANCE)
                                    .build();
                            } else {
                                sslCtx = SslContextBuilder.forClient().build();
                            }
                            SslHandler sslHandler = sslCtx.newHandler(ch.alloc(), uri.getHost(), uri.getPort());
                            pipeline.addLast(sslHandler);
                        }

                        pipeline.addLast(new IdleStateHandler(60, 0, 0, TimeUnit.SECONDS));

                        if (enableLoggingHandler) {
                            pipeline.addLast(new LoggingHandler("DEBUG"));
                        }

                        WebSocketClientHandshaker handshaker = WebSocketClientHandshakerFactory.newHandshaker(
                            uri, WebSocketVersion.V13, null, false, new DefaultHttpHeaders());

                        WebSocketClientExtensionHandler extensionHandler = getWebSocketClientExtensionHandler();
                        WebSocketClientProtocolHandler wsHandler = new WebSocketClientProtocolHandler(handshaker, extensionHandler);
                        pipeline.addLast(wsHandler);

                        WebSocketClientHandler clientHandler = getWebSocketClientHandler(handshaker, messageHandler);
                        pipeline.addLast(clientHandler);
                    }
                });

            ChannelFuture future = bootstrap.connect(new InetSocketAddress(uri.getHost(), uri.getPort())).sync();
            webSocketChannel = future.channel();
            future.channel().closeFuture().sync();
        } catch (Exception e) {
            LOG.error("Connection error", e);
        } finally {
            disconnect();
        }
    }

    public void disconnect() {
        if (webSocketChannel != null) {
            webSocketChannel.close();
        }
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully();
        }
    }

    public WebSocketClientExtensionHandler getWebSocketClientExtensionHandler() {
        if (compressedMessages) {
            return new PerMessageDeflateClientExtensionHandler(true, true, 15);
        } else {
            return new WebSocketClientExtensionHandler() {
                @Override
                public List<WebSocketExtensionData> getRequestedExtensions() {
                    return Collections.emptyList();
                }

                @Override
                public WebSocketExtension getExtension() {
                    return null;
                }
            };
        }
    }

    public WebSocketClientHandler getWebSocketClientHandler(WebSocketClientHandshaker handshaker, WebSocketMessageHandler messageHandler) {
        return new WebSocketClientHandler(handshaker, messageHandler);
    }

    public static class WebSocketClientHandler extends SimpleChannelInboundHandler<WebSocketFrame> {
        private final WebSocketClientHandshaker handshaker;
        private final WebSocketMessageHandler messageHandler;
        private boolean handshakeComplete;

        public WebSocketClientHandler(WebSocketClientHandshaker handshaker, WebSocketMessageHandler messageHandler) {
            this.handshaker = handshaker;
            this.messageHandler = messageHandler;
        }

        @Override
        public void handlerAdded(ChannelHandlerContext ctx) throws Exception {
            handshaker.handshakeFuture().addListener((Future<Void> future) -> {
                if (future.isSuccess()) {
                    LOG.info("WebSocket Client connected!");
                } else {
                    LOG.error("WebSocket Client failed to connect", future.cause());
                }
            });
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            LOG.info("WebSocket Client channel active");
        }

        @Override
        public void channelInactive(ChannelHandlerContext ctx) {
            LOG.info("WebSocket Client disconnected");
            handshaker = null;
        }

        @Override
        public void channelRead0(ChannelHandlerContext ctx, WebSocketFrame frame) throws Exception {
            if (frame instanceof TextWebSocketFrame) {
                messageHandler.handleMessage(((TextWebSocketFrame) frame).text());
            } else if (frame instanceof BinaryWebSocketFrame) {
                messageHandler.handleMessage(frame.content().nioBuffer());
            } else if (frame instanceof PingWebSocketFrame) {
                ctx.write(new PongWebSocketFrame(frame.content().retain()));
            } else if (frame instanceof CloseWebSocketFrame) {
                ctx.close();
            }
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            LOG.error("WebSocket Client exception", cause);
            ctx.close();
        }

        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) throws Exception {
            if (evt instanceof IdleStateEvent) {
                LOG.info("No data received for 60 seconds. Sending ping.");
                ctx.writeAndFlush(new PingWebSocketFrame(Unpooled.buffer(0)));
            } else {
                super.userEventTriggered(ctx, evt);
            }
        }
    }
}
