import java.io.*;
import java.nio.*;
import java.nio.channels.*;
import java.nio.file.*;
import java.util.concurrent.locks.*;

public class CachedOpenFile {
    private final FileChannel fc;
    private final ReadWriteLock lock = new ReentrantReadWriteLock();
    private final long capacity;

    public CachedOpenFile(Path file, long capacity) throws IOException {
        this.capacity = capacity;
        if (Files.notExists(file.getParent())) {
            Files.createDirectories(file.getParent());
        }
        this.fc = FileChannel.open(file, StandardOpenOption.CREATE, StandardOpenOption.READ, StandardOpenOption.WRITE);
    }

    public int readBlock(ByteBuffer buffer, long offset) throws IOException {
        lockForIoOperation();
        try {
            ensureOpen();
            if (offset > capacity) {
                throw new IllegalArgumentException("Offset exceeds capacity");
            }
            int remaining = buffer.remaining();
            if (offset + remaining > capacity) {
                throw new IllegalArgumentException("Read would exceed capacity");
            }
            return fc.read(buffer, offset);
        } finally {
            unlockForIoOperation();
        }
    }

    public int writeBlock(ByteBuffer buffer, long offset) throws IOException {
        lock.writeLock().lock();
        try {
            ensureOpen();
            int remaining = buffer.remaining();
            if (offset + remaining > capacity) {
                throw new IllegalArgumentException("Write would exceed capacity");
            }
            return fc.write(buffer, offset);
        } finally {
            lock.writeLock().unlock();
        }
    }

    public int writeBlock(ByteBufferView buffer, long offset) throws IOException {
        lock.writeLock().lock();
        try {
            ensureOpen();
            int remaining = buffer.remaining();
            if (offset + remaining > capacity) {
                throw new IllegalArgumentException("Write would exceed capacity");
            }
            return fc.write(buffer.getBuffer(), offset);
        } finally {
            lock.writeLock().unlock();
        }
    }

    public long capacity() {
        return capacity;
    }

    public long size() throws IOException {
        lock.readLock().lock();
        try {
            ensureOpen();
            return fc.size();
        } finally {
            lock.readLock().unlock();
        }
    }

    public void flush() throws IOException {
        lock.writeLock().lock();
        try {
            ensureOpen();
            fc.force(true);
        } finally {
            lock.writeLock().unlock();
        }
    }

    public void lockForIoOperation() {
        lock.readLock().lock();
    }

    public void unlockForIoOperation() {
        lock.readLock().unlock();
    }

    public void close() throws IOException {
        lock.writeLock().lock();
        try {
            if (fc != null && fc.isOpen()) {
                fc.close();
            }
        } finally {
            lock.writeLock().unlock();
        }
    }

    private void ensureOpen() throws IOException {
        if (fc == null || !fc.isOpen()) {
            throw new ClosedChannelException();
        }
    }
}

class ByteBufferView {
    private final ByteBuffer buffer;
    private final int position;
    private final int limit;

    public ByteBufferView(ByteBuffer buffer, int position, int limit) {
        this.buffer = buffer;
        this.position = position;
        this.limit = limit;
    }

    public ByteBuffer getBuffer() {
        return buffer;
    }

    public int remaining() {
        return limit - position;
    }
