import io.netty.bootstrap.Bootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioSocketChannel;
import io.netty.handler.ssl.SslHandler;
import io.netty.handler.timeout.ReadTimeoutHandler;
import javax.net.ssl.SSLParameters;
import java.util.concurrent.TimeUnit;

public class NettyTcpClient {
    private final Bootstrap bootstrap = new Bootstrap();
    private EventLoopGroup eventLoopGroup;
    private Integer connectTimeout;
    private Integer readTimeout;
    private String threadPrefix;

    public void init(DockerClientConfig dockerClientConfig) {
        this.eventLoopGroup = new NioEventLoopGroup(0, r -> {
            Thread thread = new Thread(r, threadPrefix + "-thread-" + System.identityHashCode(r));
            thread.setDaemon(true);
            return thread;
        });
        bootstrap.group(eventLoopGroup)
                .channel(NioSocketChannel.class)
                .option(ChannelOption.CONNECT_TIMEOUT_MILLIS, connectTimeout)
                .handler(new ChannelInitializer<Channel>() {
                    @Override
                    protected void initChannel(Channel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (dockerClientConfig.isSslEnabled()) {
                            SslHandler sslHandler = initSsl(dockerClientConfig);
                            pipeline.addLast(sslHandler);
                        }
                        pipeline.addLast(new ReadTimeoutHandler(readTimeout, TimeUnit.MILLISECONDS));
                    }
                });
    }

    public DuplexChannel connect(DockerClientConfig dockerClientConfig) throws Exception {
        ChannelFuture future = bootstrap.connect(dockerClientConfig.getHost(), dockerClientConfig.getPort()).sync();
        return (DuplexChannel) future.channel();
    }

    private SslHandler initSsl(DockerClientConfig dockerClientConfig) {
        SslHandler sslHandler = dockerClientConfig.getSslContext().newHandler(bootstrap.config().group());
        SSLParameters sslParameters = sslHandler.sslEngine().getSSLParameters();
        sslHandler.sslEngine().setSSLParameters(enableHostNameVerification(sslParameters));
        return sslHandler;
    }

    private SSLParameters enableHostNameVerification(SSLParameters sslParameters) {
        sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
        return sslParameters;
    }

    public void close() {
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully();
        }
    }

    private <T extends Channel> T configure(T channel) {
        channel.config().setOption(ChannelOption.CONNECT_TIMEOUT_MILLIS, connectTimeout);
        return channel;
    }

    public class ReadTimeoutHandler extends io.netty.handler.timeout.ReadTimeoutHandler {
        public ReadTimeoutHandler() {
            super(readTimeout, TimeUnit.MILLISECONDS);
        }
    }
}
