public class Netty4HttpServer extends AbstractServer implements StatisticCallback {
    private HttpMessageHandler httpMessageHandler;
    private URL url;
    private Channel channel;
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private StandardThreadExecutor standardThreadExecutor;

    public Netty4HttpServer(URL url, HttpMessageHandler httpMessageHandler) {
        this.url = url;
        this.httpMessageHandler = httpMessageHandler;
    }

    @Override
    public boolean open() {
        int port = url.getPort() != -1 ? url.getPort() : 8080;
        String query = url.getQuery();
        Map<String, String> params = new HashMap<>();
        if (query != null) {
            for (String param : query.split("&")) {
                String[] parts = param.split("=");
                if (parts.length == 2) {
                    params.put(parts[0], parts[1]);
                }
            }
        }
        int bossThreads = 1;
        int workerThreads = 1;
        int executorThreads = 1;
        try {
            bossThreads = Integer.parseInt(params.getOrDefault("bossThreads", "1"));
            workerThreads = Integer.parseInt(params.getOrDefault("workerThreads", "1"));
            executorThreads = Integer.parseInt(params.getOrDefault("executorThreads", "1"));
        } catch (NumberFormatException ignored) {
        }
        bossGroup = new NioEventLoopGroup(bossThreads);
        workerGroup = new NioEventLoopGroup(workerThreads);
        standardThreadExecutor = new StandardThreadExecutor(executorThreads);

        ServerBootstrap bootstrap = new ServerBootstrap();
        bootstrap.group(bossGroup, workerGroup)
                .channel(NioServerSocketChannel.class)
                .childHandler(new ChannelInitializer<>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ChannelPipeline pipeline = ch.pipeline();
                        pipeline.addLast(new HttpRequestDecoder());
                        pipeline.addLast(new HttpObjectAggregator(65536));
                        pipeline.addLast(new SimpleChannelInboundHandler<FullHttpRequest>() {
                            @Override
