                                cause.printStackTrace();
                                ctx.close();
                            }
                        });
                    }
                })
                .option(ChannelOption.SO_BACKLOG, 128)
                .childOption(ChannelOption.SO_KEEPALIVE, true);

        try {
            channel = bootstrap.bind(port).sync().channel();
            return channel.isActive();
        } catch (InterruptedException e) {
            e.printStackTrace();
            return false;
        }
    }

    @Override
    public void close(int timeout) {
        if (channel != null) {
            channel.close();
        }
        if (bossGroup != null) {
            bossGroup.shutdownGracefully(timeout, timeout, java.util.concurrent.TimeUnit.MILLISECONDS);
        }
        if (workerGroup != null) {
            workerGroup.shutdownGracefully(timeout, timeout, java.util.concurrent.TimeUnit.MILLISECONDS);
        }
        if (standardThreadExecutor != null) {
            standardThreadExecutor.shutdown(timeout);
        }
