import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.util.concurrent.DefaultEventExecutorGroup;
import org.slf4j.Logger;

import java.net.URL;
import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicInteger;

public class NettyServer {
    private final NettyServerChannelManage channelManage;
    private final EventLoopGroup bossGroup;
    private final EventLoopGroup workerGroup;
    private Channel serverChannel;
    private final MessageHandler messageHandler;
    private final ThreadPoolExecutor threadPoolExecutor;
    private final AtomicInteger rejectCounter;
    private final Logger logger;
    private final URL url;
    private ChannelState state;
    private final Codec codec;

    public NettyServer(URL url, MessageHandler messageHandler) {
        this.url = url;
        this.messageHandler = messageHandler;
        this.channelManage = new NettyServerChannelManage();
        this.bossGroup = new NioEventLoopGroup(1);
        this.workerGroup = new NioEventLoopGroup();
        this.rejectCounter = new AtomicInteger(0);
        this.logger = org.slf4j.LoggerFactory.getLogger(NettyServer.class);
        this.state = ChannelState.NEW;
        this.codec = new Codec(); // Assuming default codec

        int corePoolSize = Integer.parseInt(url.getParameter("corePoolSize", "10"));
        int maxPoolSize = Integer.parseInt(url.getParameter("maxPoolSize", "20"));
        long keepAliveTime = Long.parseLong(url.getParameter("keepAliveTime", "60"));
        int queueCapacity = Integer.parseInt(url.getParameter("queueCapacity", "100"));

        BlockingQueue<Runnable> queue = new LinkedBlockingQueue<>(queueCapacity);
        threadPoolExecutor = new ThreadPoolExecutor(
                corePoolSize,
                maxPoolSize,
                keepAliveTime,
                TimeUnit.SECONDS,
                queue,
                new ThreadPoolExecutor.CallerRunsPolicy() {
                    @Override
                    public void rejectedExecution(Runnable r, ThreadPoolExecutor e) {
                        rejectCounter.incrementAndGet();
                        super.rejectedExecution(r, e);
                    }
                }
        );
    }

    public boolean open() {
        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_KEEPALIVE, true)
                    .childHandler(new ChannelInitializer<>() {
                        @Override
                        protected void initChannel(Channel ch) {
                            ChannelPipeline pipeline = ch.pipeline();
                            pipeline.addLast(new CodecHandler(codec));
                            pipeline.addLast(new DefaultEventExecutorGroup(threadPoolExecutor), new MessageProcessingHandler(messageHandler, rejectCounter, logger));
                        }
                    });

            String portStr = url.getParameter("port", "8080");
            int port = Integer.parseInt(portStr);
            serverChannel = bootstrap.bind(port).sync().channel();
            state = ChannelState.ACTIVE;
            logger.info("Server started on port {}", port);
            return true;
        } catch (Exception e) {
            logger.error("Failed to start server", e);
            cleanup();
            return false;
        }
    }

    public void close(int timeout) {
        if (state == ChannelState.CLOSED) return;

        try {
            serverChannel.close().sync();
        } catch (InterruptedException e) {
            logger.warn("Interrupted while closing server channel", e);
        }

        bossGroup.shutdownGracefully();
        workerGroup.shutdownGracefully();

        try {
            threadPoolExecutor.shutdown();
            if (!threadPoolExecutor.awaitTermination(timeout, TimeUnit.SECONDS)) {
                threadPoolExecutor.shutdownNow();
            }
        } catch (InterruptedException e) {
            threadPoolExecutor.shutdownNow();
            Thread.currentThread().interrupt();
        }

        cleanup();
    }

    private void cleanup() {
        state = ChannelState.CLOSED;
        channelManage.clear();
        logger.info("Server resources cleaned up");
    }

    private enum ChannelState {
        NEW, ACTIVE, CLOSED
    }

    private static class CodecHandler extends ChannelInboundHandlerAdapter {
        private final Codec codec;

        public CodecHandler(Codec codec) {
            this.codec = codec;
        }

        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            try {
                ctx.fireChannelRead(codec.decode((byte[]) msg));
            } finally {
                ReferenceCountUtil.release(msg);
            }
        }
    }

    private static class MessageProcessingHandler extends ChannelInboundHandlerAdapter {
        private final MessageHandler messageHandler;
        private final AtomicInteger rejectCounter;
        private final Logger logger;

        public MessageProcessingHandler(MessageHandler messageHandler, AtomicInteger rejectCounter, Logger logger) {
            this.messageHandler = messageHandler;
            this.rejectCounter = rejectCounter;
            this.logger = logger;
        }

        @Override
        public void channelRead(final ChannelHandlerContext ctx, Object msg) {
            messageHandler.handle(msg, new ChannelFutureListener() {
                @Override
                public void operationComplete(ChannelFuture future) {
                    if (!future.isSuccess()) {
                        logger.error("Message processing failed", future.cause());
                    }
                }
            });
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            logger.error("Exception caught in channel", cause);
            ctx.close();
        }
    }
}

