import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.timeout.IdleStateHandler;
import io.netty.util.concurrent.DefaultEventExecutorGroup;
import io.netty.util.concurrent.EventExecutorGroup;

import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.TimeUnit;

public class MysqlNettyServer {
    private String host;
    private int port;
    private static final Map<String, MysqlConnection> connections = new ConcurrentHashMap<>();
    private EventLoopGroup eventLoopGroup;
    private ServerBootstrap server;

    public MysqlNettyServer(String host, int port) {
        this.host = host;
        this.port = port;
    }

    public void start() throws Exception {
        eventLoopGroup = new NioEventLoopGroup();
        EventExecutorGroup businessGroup = new DefaultEventExecutorGroup(4);
        server = new ServerBootstrap();
        server.group(eventLoopGroup)
              .channel(NioServerSocketChannel.class)
              .option(ChannelOption.SO_BACKLOG, 128)
              .childOption(ChannelOption.SO_KEEPALIVE, true)
              .childHandler(channelInitializer(businessGroup));

        ChannelFuture future = server.bind(host, port).sync();
        future.channel().closeFuture().sync();
    }

    private ChannelInitializer<SocketChannel> channelInitializer(EventExecutorGroup businessGroup) {
        return new ChannelInitializer<SocketChannel>() {
            @Override
            protected void initChannel(SocketChannel ch) {
                ChannelPipeline pipeline = ch.pipeline();
                pipeline.addLast(new LengthFieldBasedFrameDecoder(16 * 1024 * 1024, 0, 3, 1, 4));
                pipeline.addLast(new MySQLMessageDecoder());
                pipeline.addLast(new IdleStateHandler(60, 30, 0, TimeUnit.SECONDS));
                pipeline.addLast(businessGroup, new MySQLCommandHandler());
                pipeline.addLast(new MySQLExceptionHandler());
                pipeline.addLast(new MySQLHandshakeHandler());
            }
        };
    }

    public void close() {
        for (MysqlConnection conn : connections.values()) {
            conn.close();
        }
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully();
        }
    }

    public static Map<String, MysqlConnection> getConnections() {
        return connections;
    }
}

class MySQLHandshakeHandler extends ChannelInboundHandlerAdapter {
    @Override
    public void channelActive(ChannelHandlerContext ctx) {
        MysqlConnection connection = new MysqlConnection(ctx.channel());
        String connectionId = connection.getConnectionId();
        MysqlNettyServer.getConnections().put(connectionId, connection);
    }

    @Override
    public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
        ctx.close();
    }
}

class MySQLMessageDecoder extends ByteToMessageDecoder {
    @Override
    protected void decode(ChannelHandlerContext ctx, ByteBuf in, List<Object> out) {
        if (in.readableBytes() < 4) return;
        in.markReaderIndex();
        int packetLength = in.readInt() & 0xFFFFFF;
        int sequence = in.readByte() & 0xFF;
        if (in.readableBytes() < packetLength) {
            in.resetReaderIndex();
            return;
        }
        ByteBuf frame = in.readBytes(packetLength);
        out.add(new MySQLPacket(frame, sequence));
    }
}

class MySQLPacket {
    private final ByteBuf content;
    private final int sequence;

    public MySQLPacket(ByteBuf content, int sequence) {
        this.content = content;
        this.sequence = sequence;
    }

    public ByteBuf getContent() {
        return content;
    }

    public int getSequence() {
        return sequence;
    }
}

class MySQLCommandHandler extends SimpleChannelInboundHandler<MySQLPacket> {
    @Override
    protected void channelRead0(ChannelHandlerContext ctx, MySQLPacket packet) {
        ByteBuf content = packet.getContent();
        if (content.readableBytes() > 0) {
            byte command = content.readByte();
            switch (command) {
                case 3: // COM_QUERY
                    handleQueryCommand(ctx, content);
                    break;
                default:
                    sendErrorResponse(ctx, "Unsupported command: " + command);
            }
        }
    }

    private void handleQueryCommand(ChannelHandlerContext ctx, ByteBuf content) {
        int length = content.readableBytes();
        byte[] query = new byte[length];
        content.readBytes(query);
        sendOkResponse(ctx);
    }

    private void sendOkResponse(ChannelHandlerContext ctx) {
        ByteBuf response = ctx.alloc().buffer(5);
        response.writeByte(0x00); // OK packet header
        response.writeInt(0x00000002); // Affected rows, last insert ID
        ctx.writeAndFlush(response);
    }

    private void sendErrorResponse(ChannelHandlerContext ctx, String message) {
        ByteBuf response = ctx.alloc().buffer(1 + 1 + message.length());
        response.writeByte(0xff); // Error packet marker
        response.writeByte(0x04); // MySQL error code
        response.writeBytes(message.getBytes());
        ctx.writeAndFlush(response);
    }

    @Override
    public void userEventTriggered(ChannelHandlerContext ctx, Object evt) {
        if (evt instanceof IdleStateEvent) {
            ctx.close();
        }
    }
}

class MySQLExceptionHandler extends ChannelInboundHandlerAdapter {
    @Override
    public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
        MysqlConnection connection = MysqlConnection.getConnection(ctx.channel());
        if (connection != null) {
            MysqlNettyServer.getConnections().remove(connection.getConnectionId());
        }
        ctx.close();
    }
}

class MysqlConnection {
    private final Channel channel;
    private final String connectionId;

    public MysqlConnection(Channel channel) {
        this.channel = channel;
        this.connectionId = Integer.toHexString(System.identityHashCode(channel));
    }

    public String getConnectionId() {
        return connectionId;
    }

    public static MysqlConnection getConnection(Channel channel) {
        return new MysqlConnection(channel);
    }

    public void close() {
        if (channel.isOpen()) {
            channel.close();
        }
    }
}
