import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.timeout.IdleStateHandler;

import java.lang.reflect.Method;
import java.util.List;
import java.util.concurrent.TimeUnit;

public class NettyServer {
    private Thread thread;
    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Channel serverChannel;
    private XxlRpcProviderFactory xxlRpcProviderFactory;

    public void start(XxlRpcProviderFactory xxlRpcProviderFactory) {
        this.xxlRpcProviderFactory = xxlRpcProviderFactory;
        thread = new Thread(() -> {
            try {
                bossGroup = new NioEventLoopGroup();
                workerGroup = new NioEventLoopGroup();
                ServerBootstrap bootstrap = new ServerBootstrap();
                bootstrap.group(bossGroup, workerGroup)
                        .channel(NioServerSocketChannel.class)
                        .childHandler(new ChannelInitializer<Channel>() {
                            @Override
                            protected void initChannel(Channel ch) {
                                ChannelPipeline pipeline = ch.pipeline();
                                pipeline.addLast(new IdleStateHandler(60, 0, 0, TimeUnit.SECONDS));
                                pipeline.addLast(new MessageToMessageDecoder<ByteBuf>() {
                                    @Override
                                    protected void decode(ChannelHandlerContext ctx, ByteBuf msg, List<Object> out) {
                                        byte[] data = new byte[msg.readableBytes()];
                                        msg.getBytes(msg.readerIndex(), data);
                                        try {
                                            out.add(xxlRpcProviderFactory.getSerializer().readObject(data));
                                        } finally {
                                            msg.release();
                                        }
                                    }
                                });
                                pipeline.addLast(new MessageToMessageEncoder<Object>() {
                                    @Override
                                    protected void encode(ChannelHandlerContext ctx, Object msg, List<Object> out) {
                                        byte[] data = xxlRpcProviderFactory.getSerializer().writeObject(msg);
                                        out.add(ctx.alloc().buffer(data.length).writeBytes(data));
                                    }
                                });
                                pipeline.addLast(new ChannelInboundHandlerAdapter() {
                                    @Override
                                    public void channelRead(final ChannelHandlerContext ctx, Object msg) {
                                        xxlRpcProviderFactory.getExecutor().submit(() -> {
                                            try {
                                                RpcRequest request = (RpcRequest) msg;
                                                Object result = processRequest(request);
                                                ctx.writeAndFlush(result);
                                            } catch (Exception e) {
                                                ctx.writeAndFlush(new RpcResponse(e));
                                            } finally {
                                                ctx.channel().close();
                                            }
                                        });
                                    }
                                    @Override
                                    public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
                                        cause.printStackTrace();
                                        ctx.close();
                                    }
                                });
                            }
                        });
                int port = xxlRpcProviderFactory.getPort();
                serverChannel = bootstrap.bind(port).sync().channel();
                serverChannel.closeFuture().sync();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            } finally {
                if (workerGroup != null) workerGroup.shutdownGracefully();
                if (bossGroup != null) bossGroup.shutdownGracefully();
            }
        });
        thread.start();
    }

    public void stop() {
        if (serverChannel != null) serverChannel.close();
        if (workerGroup != null) workerGroup.shutdownGracefully();
        if (bossGroup != null) bossGroup.shutdownGracefully();
        if (thread != null) thread.interrupt();
    }

    private Object processRequest(RpcRequest request) throws Exception {
        Object service = xxlRpcProviderFactory.getProvider(request.getInterfaceName());
        Method method = service.getClass().getMethod(
                request.getMethodName(),
                request.getParameterTypes()
        );
        return method.invoke(service, request.getParameters());
    }
}
