    private final Map<ChannelOption, Object> channelOptions;
    private final Map<ChannelOption, Object> childChannelOptions;
    private final List<ChannelHandler> httpChannelHandlers;

    public SofaNettyJaxrsServer(ServerConfig serverConfig, String hostname, int port, ResteasyDeployment deployment, String root, SecurityDomain domain) {
        this.serverConfig = serverConfig;
        this.bootstrap = new ServerBootstrap();
        this.hostname = hostname;
        this.port = port;
        this.deployment = deployment;
        this.root = root;
        this.domain = domain;
        this.ioWorkerCount = serverConfig.getIoWorkerCount();
        this.executorThreadCount = serverConfig.getExecutorThreadCount();
        this.sslContext = serverConfig.getSslContext();
        this.maxRequestSize = serverConfig.getMaxRequestSize();
        this.backlog = serverConfig.getBacklog();
        this.channelHandlers = serverConfig.getChannelHandlers();
        this.channelOptions = serverConfig.getChannelOptions();
        this.childChannelOptions = serverConfig.getChildChannelOptions();
        this.httpChannelHandlers = serverConfig.getHttpChannelHandlers();
    }

    public void start() throws Exception {
        if (Epoll.isAvailable()) {
            eventLoopGroup = new EpollEventLoopGroup(ioWorkerCount);
            eventExecutor = new EpollEventLoopGroup(executorThreadCount);
        } else {
            eventLoopGroup = new NioEventLoopGroup(ioWorkerCount);
            eventExecutor = new NioEventLoopGroup(executorThreadCount);
        }

        bootstrap.group(eventLoopGroup, eventExecutor)
                .channel(Epoll.isAvailable() ? EpollServerSocketChannel.class : NioServerSocketChannel.class)
                .option(ChannelOption.SO_BACKLOG, backlog)
                .childOption(ChannelOption.SO_KEEPALIVE, Boolean.TRUE)
                .handler(new LoggingHandler(LogLevel.INFO))
                .childHandler(createChannelInitializer());

        ChannelFuture future = bootstrap.bind(hostname, port).sync();
        future.channel().closeFuture().sync();
    }

    public void stop() {
        if (eventLoopGroup != null) {
            eventLoopGroup.shutdownGracefully();
        }
        if (eventExecutor != null) {
            eventExecutor.shutdownGracefully();
        }
        if (deployment != null) {
            try {
                deployment.stop();
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }

    private ChannelInitializer<SocketChannel> createChannelInitializer() {
        return new ChannelInitializer<SocketChannel>() {
