        pipeline.addLast("codec", new HttpServerCodec());
        pipeline.addLast("aggregator", new HttpServerCodec());
        pipeline.addLast("chunkedWriter", new ChunkedWriteHandler());
        pipeline.addLast("expectContinue", new HttpServerExpectContinueHandler());
        pipeline.addLast("dispatcher", dispatcher);

        if (httpChannelHandlers != null) {
            for (ChannelHandler handler : httpChannelHandlers) {
                pipeline.addLast(handler);
            }
        }
    }

    private RequestDispatcher createRequestDispatcher() throws Exception {
        deployment.setSecurityDomain(domain);
        deployment.setRootResourcePath(root);
        deployment.start();
        return new RequestDispatcher(deployment.getDispatcher());
    }
