import io.netty.handler.codec.http.FullHttpRequest;
import io.netty.handler.codec.http.FullHttpResponse;
import io.netty.handler.codec.http.HttpObjectAggregator;
import io.netty.handler.codec.http.HttpServerCodec;
import io.netty.handler.codec.http.HttpVersion;
import io.netty.util.concurrent.GenericFutureListener;

public class EmbedHttpServer {
    private static final Logger LOGGER = Logger.getLogger(EmbedHttpServer.class.getName());

    private final int port;
    private final DefaultController defaultController;
    private final List<Object> controllerList = new ArrayList<>();

    public @interface RequestMapping {
        String value();
    }

    public EmbedHttpServer(int port) {
        this.port = port;
        this.defaultController = new DefaultController();
    }

    public FullHttpResponse invoke(String uri, Channel channel, FullHttpRequest request) {
        String normalizedUri = fixUri(uri);
        for (Object controller : controllerList) {
            for (Method method : controller.getClass().getMethods()) {
                if (method.isAnnotationPresent(RequestMapping.class)) {
                    RequestMapping mapping = method.getAnnotation(RequestMapping.class);
                    String path = fixUri(mapping.value());
                    if (normalizedUri.equals(path)) {
                        try {
                            return (FullHttpResponse) method.invoke(controller, normalizedUri, channel, request);
                        } catch (Exception e) {
                            LOGGER.severe("Error invoking controller method: " + e.getMessage());
                            FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, io.netty.handler.codec.http.HttpResponseStatus.INTERNAL_SERVER_ERROR);
                            return response;
                        }
                    }
                }
            }
        }
        try {
            Method defaultMethod = defaultController.getClass().getMethod("handleRequest", String.class, Channel.class, FullHttpRequest.class);
            return (FullHttpResponse) defaultMethod.invoke(defaultController, normalizedUri, channel, request);
        } catch (Exception e) {
            LOGGER.severe("Error in default controller: " + e.getMessage());
            return new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, io.netty.handler.codec.http.HttpResponseStatus.NOT_FOUND);
        }
    }

    private String fixUri(String uri) {
        if (uri == null || uri.isEmpty()) {
            return "/";
        }
        if (!uri.startsWith("/")) {
            uri = "/" + uri;
        }
        return uri;
    }

    public void start(GenericFutureListener<?> startedListener) {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();

