    private void doWrite(Buffer buffer, long position, Handler<AsyncResult<Void>> handler) {
        Objects.requireNonNull(buffer, "buffer");
        Arguments.require(position >= 0, "position must be >= 0");
        check();
        ByteBuf buf = ((BufferInternal)buffer).getByteBuf();
        if (buf.nioBufferCount() > 1) {
            doWrite(buf.nioBuffers(), position, handler);
        } else {
            ByteBuffer bb = buf.nioBuffer();
            doWrite(bb, position, bb.limit(), handler);
        }
    }

    private void doWrite(ByteBuffer[] buffers, long position, Handler<AsyncResult<Void>> handler) {
        AtomicInteger cnt = new AtomicInteger();
        AtomicBoolean sentFailure = new AtomicBoolean();
        for (ByteBuffer b: buffers) {
            int limit = b.limit();
            doWrite(b, position, limit, ar -> {
                if (ar.succeeded()) {
                    if (cnt.incrementAndGet() == buffers.length) {
                        handler.handle(ar);
                    }
                } else {
                    if (sentFailure.compareAndSet(false, true)) {
                        handler.handle(ar);
                    }
                }
            });
            position += limit;
        }
    }

    private void doWrite(ByteBuffer buff, long position, long toWrite, Handler<AsyncResult<Void>> handler) {
        if (toWrite > 0) {
            writeInternal(buff, position, handler);
        } else {
            handler.handle(Future.succeededFuture());
        }
    }

    private void writeInternal(ByteBuffer buff, long position, Handler<AsyncResult<Void>> handler) {
        ch.write(buff, position, null, new CompletionHandler<Integer, Object>() {
            public void completed(Integer bytesWritten, Object attachment) {
                long pos = position;
                if (buff.hasRemaining()) {
                    pos += bytesWritten;
                    writeInternal(buff, pos, handler);
                } else {
                    context.runOnContext((v) -> {
                        handler.handle(Future.succeededFuture());
                    });
                }
            }

            public void failed(Throwable exc, Object attachment) {
                context.runOnContext((v) -> {
                    handler.handle(Future.failedFuture(exc));
                });
            }
        });
    }

    private void doRead(Buffer writeBuff, int offset, ByteBuffer buff, long position, Promise<Buffer> promise) {
        ch.read(buff, position, null, new CompletionHandler<Integer, Object>() {
            long pos = position;

            private void done() {
                buff.flip();
                writeBuff.setBytes(offset, buff);
                buff.compact();
                promise.complete(writeBuff);
            }

            public void completed(Integer bytesRead, Object attachment) {
                if (bytesRead == -1) {
                    done();
                } else if (buff.hasRemaining()) {
                    pos += bytesRead;
                    doRead(writeBuff, offset, buff, pos, promise);
                } else {
                    done();
                }
            }

            public void failed(Throwable t, Object attachment) {
                promise.fail(t);
            }
        });
    }

    private void check() {
        if (closed) {
            throw new IllegalStateException("File handle is closed");
        }
    }

    private void closeInternal(Promise<Void> handler) {
        check();
        closed = true;
        context.<Void>executeBlockingInternal(() -> {
            ch.close();
            return null;
        }).onComplete(handler);
    }
