import io.vertx.core.impl.ContextInternal;
import io.vertx.core.impl.FileResolver;

public class AsyncFileImpl implements AsyncFile {

  private final VertxInternal vertx;
  private final AsynchronousFileChannel ch;
  private final ContextInternal context;
  private final int readBufferSize = 8192;
  private final long readLength = Long.MAX_VALUE;
  private volatile boolean closed;
  private volatile long writePos;
  private volatile long readPos;
  private Handler<Buffer> handler;
  private Handler<Void> endHandler;

  public AsyncFileImpl(VertxInternal vertx, String path, OpenOptions options, ContextInternal context) throws IOException {
    this.vertx = vertx;
    this.context = context;
    Set<StandardOpenOption> openOptions = new HashSet<>();
    if (options.isRead()) {
      openOptions.add(StandardOpenOption.READ);
    }
    if (options.isWrite()) {
      openOptions.add(StandardOpenOption.WRITE);
    }
    if (options.isAppend()) {
      openOptions.add(StandardOpenOption.APPEND);
    }
    if (options.isCreateNew()) {
      openOptions.add(StandardOpenOption.CREATE_NEW);
    }
    if (options.isCreate()) {
      openOptions.add(StandardOpenOption.CREATE);
    }
    if (options.isTruncateExisting()) {
      openOptions.add(StandardOpenOption.TRUNCATE_EXISTING);
    }
    Path filePath = FileResolver.getDefault().getPath(vertx, path);
    ExecutorService executor = vertx.getThreadPoolExecutor();
    this.ch = AsynchronousFileChannel.open(filePath, openOptions, executor);
  }

  public Future<Void> close() {
    Promise<Void> promise = Promise.promise();
    closeInternal(promise);
    return promise.future();
  }

  private void closeInternal(Promise<Void> handler) {
    if (ch != null && !closed) {
      try {
        ch.close();
      } catch (IOException e) {
        handler.fail(e);
        return;
      }
      closed = true;
      context.runOnContext(v -> handler.complete());
    } else {
      handler.complete();
    }
  }

  public Future<Buffer> read(Buffer buffer, int offset, long position, int length) {
    Promise<Buffer> promise = Promise.promise();
    check();
    ByteBuffer byteBuffer = ByteBuffer.allocate(length);
    doRead(buffer, offset, byteBuffer, position, promise);
    return promise.future();
  }

  private void doRead(Buffer writeBuff, int offset, ByteBuffer buff, long position, Promise<Buffer> promise) {
    ch.read(buff, position, buff, new CompletionHandler<Integer, ByteBuffer>() {
      @Override
      public void completed(Integer result, ByteBuffer attachment) {
        context.executeOnContext(v -> {
          if (result == -1) {
            if (endHandler != null) {
              endHandler.handle(null);
            }
            promise.complete(writeBuff);
          } else {
            readPos += result;
            if (handler != null) {
              Buffer data = Buffer.buffer(attachment.array(), 0, result);
              handler.handle(data);
            }
            promise.complete(writeBuff);
          }
        });
      }

      @Override
      public void failed(Throwable exc, ByteBuffer attachment) {
        context.executeOnContext(v -> promise.fail(exc));
      }
    });
  }

  public Future<Void> write(Buffer buffer, long position) {
    Promise<Void> promise = Promise.promise();
    doWrite(buffer, position, promise);
    return promise.future();
  }

  private void doWrite(Buffer buffer, long position, Promise<Void> promise) {
    ByteBuffer byteBuffer = ByteBuffer.wrap(buffer.getBytes());
    writeInternal(byteBuffer, position, promise);
  }

  private void writeInternal(ByteBuffer buff, long position, Promise<Void> promise) {
    ch.write(buff, position, buff, new CompletionHandler<Integer, ByteBuffer>() {
      @Override
      public void completed(Integer result, ByteBuffer attachment) {
        context.executeOnContext(v -> {
          writePos += result;
          promise.complete();
        });
      }

      @Override
      public void failed(Throwable exc, ByteBuffer attachment) {
        context.executeOnContext(v -> promise.fail(exc));
      }
    });
  }

  private void check() {
    if (closed) {
      throw new IllegalStateException("File is closed");
    }
  }
}

