import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslHandler;
import io.netty.handler.codec.http2.Http2FrameCodecBuilder;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public abstract class AbstractHttp2ServerTransport {
    private static final Logger LOGGER = LoggerFactory.getLogger(AbstractHttp2ServerTransport.class);
    private final ServerBootstrap serverBootstrap;
    private final ServerTransportConfig transportConfig;
    private EventLoopGroup bizGroup;
    private Channel serverChannel;

    public AbstractHttp2ServerTransport(ServerTransportConfig transportConfig) {
        this.transportConfig = transportConfig;
        this.serverBootstrap = new ServerBootstrap();
    }

    public boolean start() {
        boolean success = false;
        try {
            bizGroup = new NioEventLoopGroup(0, new DefaultThreadFactory("http2-server-biz-group"));

            serverBootstrap
                .group(bizGroup)
                .channel(NioServerSocketChannel.class)
                .option(ChannelOption.SO_BACKLOG, transportConfig.getBacklog())
                .childOption(ChannelOption.SO_KEEPALIVE, transportConfig.isKeepAlive())
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    public void initChannel(SocketChannel ch) throws Exception {
                        ChannelPipeline pipeline = ch.pipeline();
                        if (transportConfig.isSslEnabled()) {
                            SslContext sslContext = transportConfig.getSslContext();
                            SslHandler sslHandler = sslContext.newHandler(ch.alloc());
                            pipeline.addLast("ssl", sslHandler);
                        }
                        pipeline.addLast(new Http2FrameCodecBuilder().build());
                        initChannelPipeline(pipeline);
                    }
                });

            ChannelFuture bindFuture = serverBootstrap.bind(transportConfig.getHost(), transportConfig.getPort()).sync();
            if (bindFuture.isSuccess()) {
                serverChannel = bindFuture.channel();
                success = true;
                LOGGER.info("HTTP/2 server started on {}:{}", transportConfig.getHost(), transportConfig.getPort());
            } else {
                LOGGER.error("Failed to bind HTTP/2 server");
            }
        } catch (Exception e) {
            LOGGER.error("Error starting HTTP/2 server", e);
        } finally {
            if (!success) {
                stop();
            }
        }
        return success;
    }

    public void stop() {
        if (serverChannel != null) {
            try {
                serverChannel.close().sync();
            } catch (InterruptedException e) {
                LOGGER.error("Error closing server channel", e);
            }
        }
        if (bizGroup != null) {
            bizGroup.shutdownGracefully();
        }
    }

    protected abstract void initChannelPipeline(ChannelPipeline pipeline);
}
