import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.http.*;
import io.netty.handler.timeout.IdleStateHandler;
import io.netty.util.CharsetUtil;
import io.netty.buffer.ByteBuf;
import io.netty.buffer.Unpooled;
import io.netty.util.concurrent.GenericFutureListener;

import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.ThreadPoolExecutor;
import java.util.concurrent.TimeUnit;

public class EmbedServer {
    private ExecutorBiz executorBiz;
    private Thread thread;
    private final EventLoopGroup bossGroup = new NioEventLoopGroup();
    private final EventLoopGroup workerGroup = new NioEventLoopGroup();
    private ThreadPoolExecutor bizThreadPool;
    private volatile boolean started = false;

    public void start(String address, int port, String appname, String accessToken) {
        bizThreadPool = (ThreadPoolExecutor) Executors.newCachedThreadPool();
        ServerBootstrap b = new ServerBootstrap();
        b.group(bossGroup, workerGroup)
         .channel(NioServerSocketChannel.class)
         .childHandler(new ChannelInitializer<Channel>() {
             @Override
             protected void initChannel(Channel ch) {
                 ChannelPipeline pipeline = ch.pipeline();
                 pipeline.addLast(new IdleStateHandler(60, 0, 0, TimeUnit.SECONDS));
                 pipeline.addLast(new HttpServerCodec());
                 pipeline.addLast(new HttpObjectAggregator(1048576));
                 pipeline.addLast(new EmbedHttpServerHandler(executorBiz, accessToken, bizThreadPool));
             }
         });

        try {
            ChannelFuture f = b.bind(address, port).sync();
            thread = new Thread(() -> {
                try {
                    f.channel().closeFuture().sync();
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            });
            thread.start();
            started = true;
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    public void stop() {
        if (started) {
            bizThreadPool.shutdownNow();
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
            started = false;
        }
    }

    public static class EmbedHttpServerHandler extends ChannelInboundHandlerAdapter {
        private final ExecutorBiz executorBiz;
        private final String accessToken;
        private final ThreadPoolExecutor bizThreadPool;

        public EmbedHttpServerHandler(ExecutorBiz executorBiz, String accessToken, ThreadPoolExecutor bizThreadPool) {
            this.executorBiz = executorBiz;
            this.accessToken = accessToken;
            this.bizThreadPool = bizThreadPool;
        }

        @Override
        public void channelRead0(ChannelHandlerContext ctx, FullHttpRequest msg) {
            if (!msg.decoderResult().isSuccess()) {
                sendError(ctx, HttpResponseStatus.BAD_REQUEST);
                return;
            }

            if (msg.method() != HttpMethod.POST) {
                sendError(ctx, HttpResponseStatus.METHOD_NOT_ALLOWED);
                return;
            }

            String authHeader = msg.headers().get(HttpHeaderNames.AUTHORIZATION);
            if (accessToken != null && !accessToken.equals(authHeader)) {
                sendError(ctx, HttpResponseStatus.UNAUTHORIZED);
                return;
            }

            ByteBuf content = msg.content();
            String json = content.toString(CharsetUtil.UTF_8);
            RequestParam param = parseRequestParam(json);

            bizThreadPool.execute(() -> {
                executorBiz.execute(param, new RequestCallback() {
                    @Override
                    public void writeBack(String responseData) {
                        ctx.executor().execute(() -> {
                            if (!ctx.channel().isActive()) return;
                            FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK);
                            response.headers().set(HttpHeaderNames.CONTENT_TYPE, "application/json; charset=UTF-8");
                            response.content().writeBytes(responseData.getBytes(CharsetUtil.UTF_8));
                            ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
                        });
                    }
                });
            });
        }

        private RequestParam parseRequestParam(String json) {
            // Implementation depends on actual RequestParam structure
            return new RequestParam();
        }

        private void sendError(ChannelHandlerContext ctx, HttpResponseStatus status) {
            FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, status);
            response.headers().set(HttpHeaderNames.CONTENT_LENGTH, 0);
            ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }

        @Override
        public void userEventTriggered(ChannelHandlerContext ctx, Object evt) {
            if (evt instanceof IdleStateEvent) {
                ctx.close();
            }
        }
    }

    public interface RequestCallback {
        void writeBack(String responseData);
    }
}
