import io.netty.bootstrap.ServerBootstrap;
import io.netty.buffer.ByteBuf;
import io.netty.buffer.Unpooled;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.LineBasedFrameDecoder;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;
import io.netty.util.CharsetUtil;

import java.lang.reflect.Constructor;
import java.util.HashMap;
import java.util.Map;
import java.util.Random;

public class Console {
    private static Console instance;
    private final Map<String, Class<? extends Command>> commands = new HashMap<>();

    private Console() {
        registerDefaultCommands();
    }

    public static Console getInstance() {
        if (instance == null) {
            instance = new Console();
        }
        return instance;
    }

    private void registerDefaultCommands() {
        commands.put("help", HelpCommand.class);
        commands.put("hello", HelloCommand.class);
        commands.put("device", DeviceCommand.class);
    }

    public void listenOnTCP(int port) {
        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 public void initChannel(SocketChannel ch) throws Exception {
                     ch.pipeline().addLast(new LineBasedFrameDecoder(8192));
                     ch.pipeline().addLast(new StringDecoder(CharsetUtil.UTF_8));
                     ch.pipeline().addLast(new StringEncoder(CharsetUtil.UTF_8));
                     ch.pipeline().addLast(new Adapter());
                 }
             });
            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private class Adapter extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            String input = (String) msg;
            input = input.trim();
            if (input.isEmpty()) {
                return;
            }
            String[] parts = input.split(" ", 2);
            String commandName = parts[0];
            Class<? extends Command> commandClass = commands.get(commandName);
            if (commandClass == null) {
                sendString(ctx, "Unknown command: " + commandName);
                sendPrompt(ctx);
                return;
            }
            try {
                Constructor<? extends Command> constructor = commandClass.getConstructor(String.class);
                Command command = constructor.newInstance(input);
                String response = command.execute();
                sendString(ctx, response);
            } catch (Exception e) {
                sendString(ctx, "Error executing command: " + e.getMessage());
            } finally {
                sendPrompt(ctx);
            }
        }

        @Override
        public void channelActive(ChannelHandlerContext ctx) {
            sendStringL(ctx, "Welcome to the Console Server. Type 'help' for available commands.");
            sendPrompt(ctx);
        }

        @Override
        public void channelReadComplete(ChannelHandlerContext ctx) {
            ctx.flush();
        }

        private void sendStringL(ChannelHandlerContext ctx, String text) {
            if (!text.endsWith("\n")) {
                text += "\n";
            }
            sendString(ctx, text);
        }

        private void sendString(ChannelHandlerContext ctx, String text) {
            ctx.writeAndFlush(text);
        }

        private void sendPrompt(ChannelHandlerContext ctx) {
            sendStringL(ctx, "console> ");
        }
    }

    public abstract static class Command {
        protected final String command;

        public Command(String command) {
            this.command = command;
        }

        public abstract String execute();
    }

    public static class HelpCommand extends Command {
        public HelpCommand(String command) {
            super(command);
        }

        @Override
        public String execute() {
            StringBuilder sb = new StringBuilder();
            sb.append("Available commands:\n");
            Console console = Console.getInstance();
            for (String cmd : console.commands.keySet()) {
                sb.append("  ").append(cmd).append("\n");
            }
            return sb.toString();
        }
    }

    public static class HelloCommand extends Command {
        public HelloCommand(String command) {
            super(command);
        }

        @Override
        public String execute() {
            String[] greetings = {"Hello!", "Hi there!", "Greetings!"};
            return greetings[new Random().nextInt(greetings.length)];
        }
    }

    public static class DeviceCommand extends Command {
        public DeviceCommand(String command) {
            super(command);
        }

        @Override
        public String execute() {
            return "{\"device\":\"server\",\"os\":\"Linux\",\"uptime\":\"1h\"}";
        }
    }
}
