    @Value("${server.port}")
    private int port;

    @Autowired
    private NettyServerHandlerInitializer nettyServerHandlerInitializer;

    private EventLoopGroup bossGroup;
    private EventLoopGroup workerGroup;
    private Channel channel;

    @PostConstruct
    public void start() throws InterruptedException {
        bossGroup = new NioEventLoopGroup();
        workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .childHandler(nettyServerHandlerInitializer)
                    .option(ChannelOption.SO_BACKLOG, 128)
                    .childOption(ChannelOption.SO_REUSEADDR, true);

            ChannelFuture future = bootstrap.bind(port).sync();
            channel = future.channel();
            logger.info("Netty server started on port {}", port);
        } catch (InterruptedException e) {
            logger.error("Server startup interrupted", e);
            shutdownGroups();
            throw e;
        } catch (Exception e) {
            logger.error("Failed to start server", e);
            shutdownGroups();
            throw new RuntimeException("Server startup failed", e);
        }
    }

    private void shutdownGroups() {
        try {
            if (workerGroup != null) {
                workerGroup.shutdownGracefully(0, 5, TimeUnit.SECONDS).await();
            }
        } catch (InterruptedException e) {
            logger.error("Error shutting down worker group", e);
        }
        try {
            if (bossGroup != null) {
                bossGroup.shutdownGracefully(0, 5, TimeUnit.SECONDS).await();
            }
        } catch (InterruptedException e) {
            logger.error("Error shutting down boss group", e);
        }
    }

    @PreDestroy
    public void shutdown() {
        try {
            if (channel != null) {
                channel.close().sync();
            }
        } catch (InterruptedException e) {
            logger.error("Error closing channel", e);
        } finally {
            shutdownGroups();
        }
    }
}
