import io.netty.handler.codec.string.StringEncoder;
import java.util.concurrent.Executor;
import java.util.concurrent.atomic.AtomicInteger;

public class NettyServer extends AbstractServer {
    private NioEventLoopGroup bossGroup;
    private NioEventLoopGroup workerGroup;
    private Channel serverChannel;
    private MessageHandler messageHandler;
    private StandardThreadExecutor standardThreadExecutor;
    private AtomicInteger rejectCounter = new AtomicInteger(0);
    private NettyServerChannelManage channelManage = new NettyServerChannelManage();

    public NettyServer(URL url, MessageHandler messageHandler) {
        super(url);
        this.messageHandler = messageHandler;
        int threadNum = url.getParameter("threadNum", 100);
        int queueSize = url.getParameter("queueSize", 200);
        this.standardThreadExecutor = new StandardThreadExecutor(threadNum, queueSize);
    }

    @Override
    public boolean open() {
        if (isClosed()) return false;
        try {
            int bossThreads = getUrl().getParameter("bossThreads", 1);
            bossGroup = new NioEventLoopGroup(bossThreads);
            int workerThreads = getUrl().getParameter("workerThreads", Runtime.getRuntime().availableProcessors() * 2);
            workerGroup = new NioEventLoopGroup(workerThreads);

            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                    .channel(NioServerSocketChannel.class)
                    .option(ChannelOption.SO_BACKLOG, 1024)
                    .childOption(ChannelOption.SO_KEEPALIVE, true)
                    .childOption(ChannelOption.TCP_NODELAY, true)
                    .childHandler(new ChannelInitializer<Channel>() {
                        @Override
                        protected void initChannel(Channel ch) throws Exception {
                            ChannelPipeline pipeline = ch.pipeline();
                            pipeline.addLast(new StringDecoder());
                            pipeline.addLast(new StringEncoder());
                            pipeline.addLast(new MessageHandlerAdapter(messageHandler, standardThreadExecutor, rejectCounter, channelManage));
                        }
                    });

            int port = getUrl().getPort();
            serverChannel = bootstrap.bind(port).sync().channel();
            setState(State.ACTIVE);
            return true;
        } catch (Exception e) {
            close();
            return false;
        }
    }

    @Override
    public void close() {
        if (isClosed()) return;
        setState(State.CLOSE);
        if (serverChannel != null) serverChannel.close();
        if (bossGroup != null) bossGroup.shutdownGracefully();
        if (workerGroup != null) workerGroup.shutdownGracefully();
        standardThreadExecutor.shutdown();
    }

    @Override
    public boolean isBound() {
        return serverChannel != null && serverChannel.isActive();
    }

    @Override
    public Response request(Request request) throws RemotingException {
        throw new MotanFrameworkException("NettyServer does not support direct requests.");
    }

    private class MessageHandlerAdapter extends ChannelInboundHandlerAdapter {
        private final MessageHandler handler;
        private final Executor executor;
        private final AtomicInteger rejectCounter;
        private final NettyServerChannelManage channelManage;

        public MessageHandlerAdapter(MessageHandler handler, StandardThreadExecutor executor, AtomicInteger rejectCounter, NettyServerChannelManage channelManage) {
            this.handler = handler;
            this.executor = executor;
            this.rejectCounter = rejectCounter;
            this.channelManage = channelManage;
        }

        @Override
        public void channelRegistered(ChannelHandlerContext ctx) throws Exception {
            channelManage.addChannel(ctx.channel());
            super.channelRegistered(ctx);
        }

        @Override
        public void channelUnregistered(ChannelHandlerContext ctx) throws Exception {
            channelManage.removeChannel(ctx.channel());
            super.channelUnregistered(ctx);
        }

        @Override
        public void channelRead(final ChannelHandlerContext ctx, final Object msg) throws Exception {
            executor.execute(() -> {
                try {
                    Response response = handler.handle((Request) msg);
                    ctx.writeAndFlush(response);
                } catch (Exception e) {
                    ctx.close();
                    if (e instanceof RejectedExecutionException) {
                        rejectCounter.incrementAndGet();
                    }
                }
            });
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) throws Exception {
            cause.printStackTrace();
            ctx.close();
        }
    }
}
