            }
        };
        isInitialized = true;
    }

    public void startServer() {
        if (!isInitialized || isStarted) {
            return;
        }
        new Thread(nettyServerRunnable).start();
        isStarted = true;
        Runtime.getRuntime().addShutdownHook(new Thread(this::shutdownServer));
    }

    public void shutdownServer() {
        if (!isInitialized || !isStarted) {
            return;
        }
        bossGroup.shutdownGracefully();
        workerGroup.shutdownGracefully();
        isStarted = false;
    }

    private static class HttpServerHandler extends SimpleChannelInboundHandler<FullHttpRequest> {
        private final QueryRegistry queryRegistry;
        private final Subject<String, String> eventSubject;

        public HttpServerHandler(QueryRegistry queryRegistry, Subject<String, String> eventSubject) {
            this.queryRegistry = queryRegistry;
            this.eventSubject = eventSubject;
        }

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, FullHttpRequest req) throws Exception {
            FullHttpResponse response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK,
                    Unpooled.wrappedBuffer("Hello from Netty!".getBytes(CharsetUtil.UTF_8)));
            response.headers().set(HttpHeaderNames.CONTENT_TYPE, "text/plain");
            response.headers().set(HttpHeaderNames.CONTENT_LENGTH, response.content().readableBytes());
            ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }
    }
}

