import io.netty.channel.socket.SocketChannel;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.codec.LineBasedFrameDecoder;
import io.netty.handler.codec.string.StringDecoder;
import io.netty.handler.codec.string.StringEncoder;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.SelfSignedCertificate;
import java.io.IOException;

public class FactorialServer {
    private final boolean ssl;
    private final int port;
    private final SslContext sslCtx;

    public FactorialServer(boolean ssl, int port) throws IOException {
        this.ssl = ssl;
        this.port = port;
        if (ssl) {
            SelfSignedCertificate ssc = new SelfSignedCertificate();
            this.sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
        } else {
            this.sslCtx = null;
        }
    }

    public static void main(String[] args) throws Exception {
        if (args.length < 1) {
            System.err.println("Usage: FactorialServer <port> [ssl]");
            return;
        }
        int port = Integer.parseInt(args[0]);
        boolean ssl = args.length > 1 && Boolean.parseBoolean(args[1]);
        new FactorialServer(ssl, port).run();
    }

    void run() throws Exception {
        EventLoopGroup bossGroup = new NioEventLoopGroup();
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 protected void initChannel(SocketChannel ch) throws Exception {
                     ChannelPipeline pipeline = ch.pipeline();
                     if (sslCtx != null) {
                         pipeline.addLast("ssl", sslCtx.newHandler(ch));
                     }
                     pipeline.addLast(new LineBasedFrameDecoder(8192));
                     pipeline.addLast(new StringDecoder());
                     pipeline.addLast(new StringEncoder());
                     pipeline.addLast(new FactorialHandler());
                 }
             })
             .option(ChannelOption.SO_BACKLOG, 100)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } finally {
            workerGroup.shutdownGracefully();
            bossGroup.shutdownGracefully();
        }
    }

    private class FactorialHandler extends io.netty.channel.ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(io.netty.channel.ChannelHandlerContext ctx, Object msg) {
            try {
                String request = (String) msg;
                int n = Integer.parseInt(request.trim());
                long result = factorial(n);
                ctx.writeAndFlush(result + "\n");
            } catch (Exception e) {
                ctx.close();
            }
        }

        private long factorial(int n) {
            long res = 1;
            for (int i = 2; i <= n; i++) {
                res *= i;
            }
            return res;
        }

        @Override
        public void exceptionCaught(io.netty.channel.ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }
    }
}

