import io.netty.handler.ssl.util.SelfSignedCertificate;

import java.io.IOException;

public class HttpUploadServer {

    public static void main(String[] args) throws Exception {
        boolean ssl = Boolean.parseBoolean(System.getProperty("ssl", "false"));
        int port = Integer.parseInt(System.getProperty("port", ssl ? "8443" : "8080"));

        final SslContext sslCtx;
        if (ssl) {
            SelfSignedCertificate ssc = new SelfSignedCertificate();
            sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
        } else {
            sslCtx = null;
        }

        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .childHandler(new ChannelInitializer<>() {
                 @Override
                 protected void initChannel(SocketChannel ch) {
                     ChannelPipeline pipeline = ch.pipeline();
                     if (sslCtx != null) {
                         pipeline.addLast(sslCtx.newHandler(ch.alloc()));
                     }
                     pipeline.addLast(new HttpServerCodec());
                     pipeline.addLast(new HttpObjectAggregator(65536));
                     pipeline.addLast(new UploadHandler());
                 }
             })
             .option(ChannelOption.SO_BACKLOG, 100)
             .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture f = b.bind(port).sync();
            f.channel().closeFuture().sync();
        } finally {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }
    }

    private static class UploadHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) {
            if (msg instanceof FullHttpRequest) {
                FullHttpRequest request = (FullHttpRequest) msg;
                if (request.method() == HttpMethod.POST && request.decoderResult().isSuccess()) {
                    String contentType = request.headers().get(HttpHeaderNames.CONTENT_TYPE, "").toLowerCase();
                    if (contentType.contains("multipart/form-data")) {
                        try {
                            HttpPostRequestDecoder decoder = new HttpPostRequestDecoder(
                                    new DefaultHttpDataFactory(false), request);
                            for (InterfaceHttpData data : decoder.getBodyHttpData()) {
                                if (data.getHttpDataType() == HttpDataType.FileUpload) {
