import org.bouncycastle.operator.jcajce.JcaContentSignerBuilder;
import org.bouncycastle.x509.X509V3CertificateGenerator;
import org.bouncycastle.jce.provider.BouncyCastleProvider;

public class PortUnificationServer {
    private static final int PORT = 8080;

    public static void main(String[] args) throws Exception {
        Security.addProvider(new BouncyCastleProvider());
        KeyPair keyPair = generateKeyPair();
        X509Certificate certificate = generateSelfSignedCert(keyPair);
        java.security.KeyStore keyStore = java.security.KeyStore.getInstance("JKS");
        keyStore.load(null, "password".toCharArray());
        keyStore.setKeyEntry("server", keyPair.getPrivate(), "password".toCharArray(), new Certificate[]{certificate});
        SslContext sslContext = SslContextBuilder.forServer(keyStore, "password", "password")
                .sslProvider(SslProvider.JDK)
                .build();

        EventLoopGroup bossGroup = new NioEventLoopGroup(1);
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
             .channel(NioServerSocketChannel.class)
             .localAddress(PORT)
             .childHandler(new ChannelInitializer<SocketChannel>() {
                 @Override
                 public void initChannel(SocketChannel ch) throws Exception {
                     ch.pipeline().addLast(new PortUnificationHandler(sslContext));
                     ch.pipeline().addLast(new SimpleChannelInboundHandler<ByteBuf>() {
                         @Override
                         public void channelRead0(ChannelHandlerContext ctx, ByteBuf msg) throws Exception {
                             System.out.println("Received: " + msg.toString(CharsetUtil.UTF_8));
                             ctx.writeAndFlush(msg);
                         }
                     });
                 }
             });

            ChannelFuture f = b.bind().sync();
            System.out.println("Server started on port " + PORT);
            f.channel().closeFuture().sync();
        } finally {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }
    }

    private static KeyPair generateKeyPair() throws NoSuchAlgorithmException, InvalidAlgorithmParameterException {
        KeyPairGenerator keyGen = KeyPairGenerator.getInstance("RSA");
        keyGen.initialize(2048);
        return keyGen.generateKeyPair();
    }

    private static X509Certificate generateSelfSignedCert(KeyPair keyPair) throws Exception {
        X500Name x500Name = new X500Name("CN=localhost");
        ContentSigner contentSigner = new JcaContentSignerBuilder("SHA256WithRSAEncryption").setProvider("BC").build(keyPair.getPrivate());
        X509V3CertificateGenerator certGen = new X509V3CertificateGenerator();
        certGen.setSerialNumber(new java.math.BigInteger(64, new SecureRandom()));
        certGen.setIssuerDN(x500Name);
        certGen.setSubjectDN(x500Name);
        certGen.setNotBefore(new Date(System.currentTimeMillis() - 1000L * 60 * 60 * 24));
        certGen.setNotAfter(new Date(System.currentTimeMillis() + (1000L * 60 * 60 * 24 * 365 * 10)));
        certGen.setPublicKey(keyPair.getPublic());
        certGen.setSignatureAlgorithm("SHA256WithRSAEncryption");
        return (X509Certificate) certGen.generate(contentSigner);
    }

    static class PortUnificationHandler extends ByteToMessageDecoder {
        private final SslContext sslContext;

        public PortUnificationHandler(SslContext sslContext) {
            this.sslContext = sslContext;
        }

        @Override
        protected void decode(ChannelHandlerContext ctx, ByteBuf in, List<Object> out) throws Exception {
            if (in.readableBytes() < 5) {
                return;
            }
            in.markReaderIndex();
            byte type = in.getByte(0);
            short version = in.getShort(1);
            if (type == 0x16 && (version == 0x0301 || version == 0x0302 || version == 0x0303)) {
                in.resetReaderIndex();
                SslHandler sslHandler = sslContext.newHandler(ctx.channel().alloc());
                ctx.pipeline().addFirst("ssl", sslHandler);
                ctx.pipeline().remove(this);
                return;
            }
            in.resetReaderIndex();
            out.add(in.readRetainedSlice(in.readableBytes()));
        }
    }
}
