public class NettyHttpServer {
    private final EventLoopGroup bossGroup = new NioEventLoopGroup(1);
    private final EventLoopGroup workerGroup = new NioEventLoopGroup();
    private static final AttributeKey<TraceContext> TRACE_CONTEXT_KEY = AttributeKey.valueOf("traceContext");

    public int startServer() throws Exception {
        ServerBootstrap b = new ServerBootstrap();
        b.group(bossGroup, workerGroup)
         .channel(NioServerSocketChannel.class)
         .childHandler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) throws Exception {
                 ChannelPipeline pipeline = ch.pipeline();
                 pipeline.addLast(new HttpServerCodec());
                 pipeline.addLast(new HttpObjectAggregator(65536));
                 pipeline.addLast(new TracingDispatchHandler());
             }
         })
         .option(ChannelOption.SO_BACKLOG, 100)
         .childOption(ChannelOption.SO_KEEPALIVE, true);

        ChannelFuture f = b.bind(0).sync();
        return ((InetSocketAddress) f.channel().localAddress()).getPort();
    }

    public void stopServer() {
        bossGroup.shutdownGracefully();
        workerGroup.shutdownGracefully();
    }

    private static class TraceContext {
        final String traceId;
        final String spanId;

        TraceContext(String traceId, String spanId) {
            this.traceId = traceId;
            this.spanId = spanId;
        }
    }

    private class TracingDispatchHandler extends ChannelInboundHandlerAdapter {
        @Override
        public void channelRead(ChannelHandlerContext ctx, Object msg) throws Exception {
            if (msg instanceof FullHttpRequest) {
                FullHttpRequest req = (FullHttpRequest) msg;
                String uri = req.uri();
                FullHttpResponse response;
                TraceContext traceContext = null;

                if ("/traced".equals(uri) || "/unsampled".equals(uri)) {
                    String traceId = UUID.randomUUID().toString();
                    String spanId = UUID.randomUUID().toString();
                    traceContext = new TraceContext(traceId, spanId);
                    ctx.channel().attr(TRACE_CONTEXT_KEY).set(traceContext);
                    response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK,
                            Unpooled.wrappedBuffer((uri + " response").getBytes()));
                } else {
                    response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK,
                            Unpooled.wrappedBuffer("Hello World".getBytes()));
                }

                response.headers().set(HttpHeaderNames.CONTENT_TYPE, "text/plain");
                response.headers().set(HttpHeaderNames.CONTENT_LENGTH, response.content().readableBytes());
                ctx.writeAndFlush(response);
            } else {
                super.channelRead(ctx, msg);
            }
        }

        @Override
        public void write(ChannelHandlerContext ctx, Object msg, ChannelPromise promise) throws Exception {
            if (msg instanceof FullHttpResponse) {
                FullHttpResponse response = (FullHttpResponse) msg;
                TraceContext traceContext = ctx.channel().attr(TRACE_CONTEXT_KEY).getAndSet(null);
                if (traceContext != null) {
                    response.headers().set("X-Trace-ID", traceContext.traceId);
                    response.headers().set("X-Span-ID", traceContext.spanId);
                }
            }
            super.write(ctx, msg, promise);
        }

        @Override
        public void exceptionCaught(ChannelHandlerContext ctx, Throwable cause) {
            cause.printStackTrace();
            ctx.close();
        }
    }
}
