        byte[] header = {(byte)'F', (byte)'L', (byte)'V', 0x01, 0x05, 0x00, 0x00, 0x00, 0x09};
        fileChannel.write(ByteBuffer.wrap(header));
        fileChannel.write(ByteBuffer.allocate(4).putInt(0).flip());
    }

    public boolean writeTag(ITag tag) throws IOException {
        if (dataChannel == null) return false;
        ByteBuffer buffer = serializeTag(tag);
        dataChannel.write(buffer);
        return true;
    }

    public void close() throws IOException {
        if (dataChannel != null) {
            dataChannel.close();
        }
        if (fileChannel != null) {
            finalizeFlv();
            fileChannel.close();
        }
    }

    private long finalizeFlv() throws IOException {
        if (append) {
            // Copy existing file content up to appendOffset
            fileChannel.position(appendOffset);
            dataChannel.position(0);
            long dataSize = dataChannel.size();
            long transferred = fileChannel.transferFrom(dataChannel, appendOffset, dataSize);
            return transferred;
        } else {
            // Write data from temp file
            dataChannel.position(0);
            long dataSize = dataChannel.size();
            long transferred = fileChannel.transferFrom(dataChannel, 9, dataSize);
            // Write final previous tag size
            ByteBuffer ptBuffer = ByteBuffer.allocate(4).putInt((int) dataSize).flip();
            fileChannel.write(ptBuffer);
            return dataSize + 4;
        }
    }

    private ByteBuffer serializeTag(ITag tag) {
        // Implementation to convert ITag to ByteBuffer
        return ByteBuffer.allocate(0);
    }
}

interface ITag {
    byte getType();
    ByteBuffer getData();
    int getSize();
    long getTimestamp();
}
