import io.netty.channel.uring.IOUringDatagramChannel;
import io.netty.channel.uring.IOUringEventLoopGroup;
import io.netty.channel.nio.NioDatagramChannel;
import java.net.InetSocketAddress;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;

public class GeyserServer {
    public static final Transport TRANSPORT = compatibleTransport();
    private static final int SHUTDOWN_QUIET_PERIOD_MS = 150;
    private static final int SHUTDOWN_TIMEOUT_MS = 1000;

    private final GeyserImpl geyser;
    private final EventLoopGroup group;
    private final EventLoopGroup childGroup;
    private final Bootstrap bootstrap;
    private final EventLoopGroup playerGroup;
    private final ChannelFuture[] bootstrapFutures;

    public GeyserServer(GeyserImpl geyser, int threadCount) {
        this.geyser = geyser;
        this.group = TRANSPORT.groupFactory().get();
        this.childGroup = TRANSPORT.groupFactory().get();
        this.bootstrap = createBootstrap();
        this.playerGroup = TRANSPORT.groupFactory().get();
        this.bootstrapFutures = new ChannelFuture[0];
    }

    public CompletableFuture<Void> bind(InetSocketAddress address) {
        CompletableFuture<Void> future = new CompletableFuture<>();
        bootstrap.bind(address).addListener(f -> {
            if (f.isSuccess()) {
                future.complete(null);
            } else {
                future.completeExceptionally(f.cause());
            }
        });
        return future;
    }

    public void shutdown() {
        if (bootstrapFutures != null) {
            for (ChannelFuture f : bootstrapFutures) {
                f.channel().close();
            }
        }
        shutdownGroup(group);
        shutdownGroup(childGroup);
        shutdownGroup(playerGroup);
    }

    private void shutdownGroup(EventLoopGroup group) {
        if (group != null) {
            group.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, TimeUnit.MILLISECONDS);
        }
    }

    private Bootstrap createBootstrap() {
        return new Bootstrap()
            .group(group)
            .channel((Class<? extends Channel>) TRANSPORT.channelClass())
            .option(ChannelOption.SO_REUSEADDR, true)
            .handler(new ChannelInitializer<Channel>() {
                @Override
                protected void initChannel(Channel ch) {
                    // Add handlers
                }
            });
    }

    private static Transport compatibleTransport() {
        if (isClassAvailable("io.netty.channel.epoll.EpollDatagramChannel")) {
            return new Transport(EpollDatagramChannel.class, EpollEventLoopGroup::new);
        } else if (isClassAvailable("io.netty.channel.kqueue.KQueueDatagramChannel")) {
            return new Transport(KQueueDatagramChannel.class, KQueueEventLoopGroup::new);
        } else if (isClassAvailable("io.netty.channel.uring.IOUringDatagramChannel")) {
            return new Transport(IOUringDatagramChannel.class, IOUringEventLoopGroup::new);
        } else {
            return new Transport(NioDatagramChannel.class, NioEventLoopGroup::new);
        }
    }

    private static boolean isClassAvailable(String className) {
        try {
            Class.forName(className);
            return true;
        } catch (ClassNotFoundException e) {
            return false;
        }
    }

    record Transport(Class<?> channelClass, java.util.function.Supplier<EventLoopGroup> groupFactory) {}
}
