                    @Override
                    protected void initChannel(NioDatagramChannel ch) throws Exception {
                        ch.pipeline().addLast(new DatagramPacketHandler());
                    }
                });

        channel = bootstrap.bind(0).sync().channel();
        InetAddress address = InetAddress.getByName(host);
