import okhttp3.*;
import org.json.JSONObject;
import java.io.IOException;

public class GithubProvider {
    private final String clientId;
    private final String clientSecret;
    private final String redirectUri;
    private final OkHttpClient client = new OkHttpClient();

    public GithubProvider(String clientId, String clientSecret, String redirectUri) {
        this.clientId = clientId;
        this.clientSecret = clientSecret;
        this.redirectUri = redirectUri;
    }

    public String getAccessToken(AccessTokenDTO accessTokenDTO) {
        RequestBody formBody = new FormBody.Builder()
                .add("client_id", clientId)
                .add("client_secret", clientSecret)
                .add("code", accessTokenDTO.getCode())
                .add("redirect_uri", redirectUri)
                .add("grant_type", "authorization_code")
                .build();

        Request request = new Request.Builder()
                .url("https://github.com/login/oauth/access_token")
                .post(formBody)
                .build();

        try (Response response = client.newCall(request).execute()) {
            if (response.isSuccessful() && response.body() != null) {
                String jsonData = response.body().string();
                JSONObject json = new JSONObject(jsonData);
                return json.getString("access_token");
            } else {
                throw new IOException("Unexpected code " + response);
            }
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }

    public GithubUser getUser(String accessToken) {
        Request request = new Request.Builder()
                .url("https://api.github.com/user")
                .header("Authorization", "token " + accessToken)
                .build();

        try (Response response = client.newCall(request).execute()) {
            if (response.isSuccessful() && response.body() != null) {
                String jsonData = response.body().string();
                JSONObject json = new JSONObject(jsonData);
                GithubUser user = new GithubUser();
                user.setId(json.getLong("id"));
                user.setLogin(json.getString("login"));
                user.setName(json.optString("name"));
                user.setAvatarUrl(json.getString("avatar_url"));
                return user;
            } else {
                throw new IOException("Unexpected code " + response);
            }
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
    }
}

