import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Optional;
import com.fasterxml.jackson.databind.ObjectMapper;

public class BeaconClient {
    private final int port;
    private final String token;
    private final HttpClient httpClient;
    private final ObjectMapper objectMapper;

    public BeaconClient(int port, String token) {
        this.port = port;
        this.token = token;
        this.httpClient = HttpClient.newHttpClient();
        this.objectMapper = new ObjectMapper();
    }

    public <RES> RES performRequest(BeaconInterface<?> prov, String rawNode) throws Exception {
        String path = prov.getPath();
        String url = "http://localhost:" + port + path;

        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .header("Content-Type", "application/json")
                .header("Authorization", "Bearer " + token)
                .POST(HttpRequest.BodyPublishers.ofString(rawNode))
                .build();

        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());

        return handleResponse(response, (Class<RES>) prov.getResponseClass());
    }

    public <REQ, RES> RES performRequest(REQ req) throws Exception {
        BeaconInterface<?> prov = (BeaconInterface<?>) req;
        String path = prov.getPath();
        String url = "http://localhost:" + port + path;

        String jsonReq = objectMapper.writeValueAsString(req);

        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .header("Content-Type", "application/json")
                .header("Authorization", "Bearer " + token)
                .POST(HttpRequest.BodyPublishers.ofString(jsonReq))
                .build();

        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());

        return handleResponse(response, (Class<RES>) prov.getResponseClass());
    }

    private <RES> RES handleResponse(HttpResponse<String> response, Class<RES> resClass) throws Exception {
        int statusCode = response.statusCode();
        if (statusCode >= 200 && statusCode < 300) {
            return objectMapper.readValue(response.body(), resClass);
        } else if (statusCode >= 400 && statusCode < 500) {
            Optional<BeaconClientErrorResponse> clientError = parseClientError(response);
            if (clientError.isPresent()) {
                throw new RuntimeException("Client error: " + clientError.get().getMessage());
            } else {
                throw new RuntimeException("Unexpected client error response");
            }
        } else if (statusCode >= 500 && statusCode < 600) {
            Optional<BeaconServerErrorResponse> serverError = parseServerError(response);
            if (serverError.isPresent()) {
                throw new RuntimeException("Server error: " + serverError.get().getMessage());
            } else {
                throw new RuntimeException("Unexpected server error response");
            }
        } else {
            throw new RuntimeException("Unexpected status code: " + statusCode);
        }
    }

    public Optional<BeaconClientErrorResponse> parseClientError(HttpResponse<String> response) {
        if (response.statusCode() >= 400 && response.statusCode() < 500) {
            try {
                return Optional.of(objectMapper.readValue(response.body(), BeaconClientErrorResponse.class));
            } catch (Exception e) {
                return Optional.empty();
            }
        } else {
            return Optional.empty();
        }
    }

    public Optional<BeaconServerErrorResponse> parseServerError(HttpResponse<String> response) {
        if (response.statusCode() >= 500 && response.statusCode() < 600) {
            try {
                return Optional.of(objectMapper.readValue(response.body(), BeaconServerErrorResponse.class));
            } catch (Exception e) {
                return Optional.empty();
            }
        } else {
            return Optional.empty();
        }
    }
}
