import javax.json.JsonObject;
import javax.json.JsonObjectBuilder;
import javax.json.JsonReader;
import javax.json.JsonValue;

public class Network {
    private static final HttpClient httpClient = HttpClient.newHttpClient();

    public static HttpResponse<String> makePOSTRequest(String url, Map<Object, Object> parameters, Map<String, String> headers, boolean convertParamtersToJSON) throws IOException, InterruptedException {
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .method("POST", buildBodyPublisher(parameters, convertParamtersToJSON));

        for (Map.Entry<String, String> header : headers.entrySet()) {
            requestBuilder.header(header.getKey(), header.getValue());
        }

        if (convertParamtersToJSON) {
            requestBuilder.header("Content-Type", "application/json");
        } else {
            requestBuilder.header("Content-Type", "application/x-www-form-urlencoded");
        }

        HttpRequest request = requestBuilder.build();
        return httpClient.send(request, BodyHandlers.ofString());
    }

    private static HttpRequest.BodyPublisher buildBodyPublisher(Map<Object, Object> parameters, boolean convertParamtersToJSON) {
        if (convertParamtersToJSON) {
            return buildJsonBody(parameters);
        } else {
            return buildFormDataFromMap(parameters);
        }
    }

    private static HttpRequest.BodyPublisher buildJsonBody(Map<Object, Object> parameters) {
        JsonObjectBuilder builder = Json.createObjectBuilder();
        for (Map.Entry<Object, Object> entry : parameters.entrySet()) {
            String key = entry.getKey().toString();
            Object value = entry.getValue();
            if (value instanceof String) {
                builder.add(key, (String) value);
            } else if (value instanceof Number) {
                builder.add(key, (Number) value);
            } else if (value instanceof Boolean) {
                builder.add(key, (Boolean) value);
            } else if (value == null) {
                builder.addNull(key);
            } else {
                builder.add(key, value.toString());
            }
        }
        JsonObject jsonObject = builder.build();
        return BodyPublishers.ofString(jsonObject.toString(), StandardCharsets.UTF_8);
    }

    private static HttpRequest.BodyPublisher buildFormDataFromMap(Map<Object, Object> data) {
        StringBuilder formData = new StringBuilder();
        for (Map.Entry<Object, Object> entry : data.entrySet()) {
            if (formData.length() > 0) {
                formData.append("&");
            }
            String key = entry.getKey().toString();
            String value = entry.getValue().toString();
            try {
                String encodedKey = URLEncoder.encode(key, StandardCharsets.UTF_8.toString());
                String encodedValue = URLEncoder.encode(value, StandardCharsets.UTF_8.toString());
                formData.append(encodedKey).append("=").append(encodedValue);
            } catch (UnsupportedEncodingException e) {
                throw new RuntimeException(e);
            }
        }
        return BodyPublishers.ofString(formData.toString(), StandardCharsets.UTF_8);
    }

    public static JsonValue makeJsonRequest(String url) throws IOException, InterruptedException {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .GET()
                .build();
        HttpResponse<String> response = httpClient.send(request, BodyHandlers.ofString());
        if (failure(response.statusCode())) {
            throw new IOException("Request failed with status code: " + response.statusCode());
        }
        JsonReader reader = Json.createReader(new StringReader(response.body()));
        return reader.readValue();
    }

    private static boolean failure(int statusCode) {
        return statusCode < 200 || statusCode >= 300;
    }
}

