        return executeRequest(new Request.Builder().url(fullUrl).get().build(), 3);
    }

    public static Resp doPost(String url) {
        return executeRequest(new Request.Builder()
                .url(url)
                .post(RequestBody.create("", MediaType.get("application/x-www-form-urlencoded")))
                .build(), 3);
    }

    public static Resp doPost(String url, Map<String, String> params) {
        FormBody.Builder formBuilder = new FormBody.Builder();
        if (params != null) {
            for (Map.Entry<String, String> entry : params.entrySet()) {
                formBuilder.add(entry.getKey(), entry.getValue());
            }
        }
        RequestBody formBody = formBuilder.build();
        return executeRequest(new Request.Builder().url(url).post(formBody).build(), 3);
    }

    private static Resp executeRequest(Request request, int retryTime) {
        int retries = 0;
        while (retries <= retryTime) {
            try {
                Call call = client.newCall(request);
                Response response = call.execute();
                if (response.isSuccessful()) {
                    return Resp.builder()
                            .code(response.code())
                            .body(bodyToString(response.body()))
                            .build();
                } else {
                    return Resp.builder()
                            .code(response.code())
                            .body(bodyToString(response.body()))
                            .build();
                }
            } catch (IOException e) {
                if (retries == retryTime) {
                    return Resp.builder()
                            .code(-1)
                            .message(e.getMessage())
                            .build();
                }
                retries++;
            }
        }
        return Resp.builder().code(-1).message("Unknown error").build();
    }

    private static String buildUrlWithParams(String url, Map<String, String> params) {
        if (params == null || params.isEmpty()) {
            return url;
