import java.util.Map;
import java.util.concurrent.*;
import java.util.Queue;
import java.util.ArrayList;
import java.util.Collections;
import java.util.HashMap;
import java.util.List;
import okhttp3.Headers;
import okhttp3.OkHttpClient;
import okhttp3.Request;
import okhttp3.Response;
import okhttp3.ResponseBody;

public class HttpDownloader extends InputStream {
    private String contentType;
    private long contentLength;
    private long contentEnd;
    private Headers header;
    private int statusCode;
    private String directUrl;
    private static volatile int curConnId = 0;
    private volatile boolean closed = false;
    private int connId;
    private InputStream is;
    private Queue<Callable<InputStream>> callableQueue;
    private Queue<Future<InputStream>> futureQueue;
    private static HashMap<String, HttpDownloader> downloaderMap = new HashMap<>();
    private ExecutorService executorService;
    private boolean supportRange;
    private int blockSize = 10 * 1024 * 1024;
    private int threadNum = 2;
    private String cookie;
    private String referer;
    private int blockCounter = 0;
    private OkHttpClient downloadClient;
    private static OkHttpClient defaultClient;

    static {
        defaultClient = new OkHttpClient.Builder()
                .connectionPool(new okhttp3.ConnectionPool(5, 1, TimeUnit.MINUTES))
                .build();
    }

    public HttpDownloader(Map<String, String> params) {
        String url = params.get("url");
        Map<String, String> headers = new HashMap<>();
        if (params.containsKey("threadNum")) {
            threadNum = Integer.parseInt(params.get("threadNum"));
        }
        if (params.containsKey("blockSize")) {
            blockSize = Integer.parseInt(params.get("blockSize"));
        }
        cookie = params.get("cookie");
        referer = params.get("referer");
        createDownloadTask(url, headers);
    }

    private void createDownloadTask(String url, Map<String, String> headers) {
        Request request = new Request.Builder()
                .url(url)
                .head()
                .build();

        try (Response response = defaultClient.newCall(request).execute()) {
            if (!response.isSuccessful()) throw new IOException("Unexpected code " + response);

            header = response.headers();
            statusCode = response.code();
            directUrl = response.request().url().toString();
            contentLength = response.body() != null ? response.body().contentLength() : 0;
            supportRange = "bytes".equals(header.get("Accept-Ranges"));
        }

        if (supportRange && threadNum > 1) {
            int numBlocks = threadNum;
            futures = new ArrayList<>();
            blockStreams = new ArrayList<>(Collections.nCopies(numBlocks, null));
            executorService = Executors.newFixedThreadPool(numBlocks);

            for (int i = 0; i < numBlocks; i++) {
                long start = (i * contentLength) / numBlocks;
                long end = ((i + 1) * contentLength) / numBlocks - 1;
                if (i == numBlocks - 1) {
                    end = contentLength - 1;
                }
                String range = "bytes=" + start + "-" + end;
                Callable<InputStream> task = () -> downloadTask(url, headers, range, i);
                Future<InputStream> future = executorService.submit(task);
                futures.add(future);
            }
        } else {
            numBlocks = 1;
            futures = new ArrayList<>();
            blockStreams = new ArrayList<>(Collections.singletonList(null));
            Callable<InputStream> task = () -> downloadTask(url, headers, null, 0);
            Future<InputStream> future = executorService.submit(task);
            futures.add(future);
        }
    }

    private InputStream downloadTask(String url, Map<String, String> headers, String range, int sliceNum) throws IOException {
        Request.Builder requestBuilder = new Request.Builder()
                .url(url)
                .get();

        if (range != null) {
            requestBuilder.header("Range", range);
        }

        if (cookie != null) {
            requestBuilder.header("Cookie", cookie);
        }

        if (referer != null) {
            requestBuilder.header("Referer", referer);
        }

        Request request = requestBuilder.build();

        try (Response response = defaultClient.newCall(request).execute()) {
            if (!response.isSuccessful()) throw new IOException("Download failed: " + response);

            ResponseBody body = response.body();
            if (body == null) throw new IOException("No response body");

            return body.byteStream();
        }
    }

    private List<Future<InputStream>> futures;
    private List<InputStream> blockStreams;
    private int currentBlockIndex = 0;
    private int numBlocks;

    @Override
    public int read(byte[] buffer, int off, int len) throws IOException {
        if (closed) {
            throw new IOException("Stream closed");
        }

        int totalRead = 0;
        while (totalRead < len) {
            if (currentBlockIndex >= numBlocks) {
                return totalRead == 0 ? -1 : totalRead;
            }

            InputStream blockStream = blockStreams.get(currentBlockIndex);
