            int bytesRead = blockStream.read(buffer, off + totalRead, len - totalRead);
            if (bytesRead == -1) {
                try {
                    blockStream.close();
                } catch (IOException e) {
                    // Ignore
                }
                currentBlockIndex++;
                continue;
            }

            totalRead += bytesRead;
        }

        return totalRead;
    }

    @Override
    public int read() throws IOException {
