import java.io.IOException;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.concurrent.TimeUnit;
import okhttp3.Credentials;
import okhttp3.OkHttpClient;
import okhttp3.Request;
import okhttp3.Response;

public class Scraper {
    private final OkHttpClient client;

    public Scraper() {
        this.client = new OkHttpClient.Builder()
            .retryOnConnectionFailure(true)
            .build();
    }

    public List<String> scrape(String url, long timeoutMillis) {
        return scrapeInternal(url, null, null, timeoutMillis);
    }

    public List<String> scrape(String url, String user, String password, long timeoutMillis) {
        return scrapeInternal(url, user, password, timeoutMillis);
    }

    private List<String> scrapeInternal(String url, String user, String password, long timeoutMillis) {
        OkHttpClient clientWithTimeout = client.newBuilder()
            .callTimeout(timeoutMillis, TimeUnit.MILLISECONDS)
            .build();
        Request.Builder requestBuilder = new Request.Builder().url(url);
        if (user != null && password != null) {
            String credential = Credentials.basic(user, password);
            requestBuilder.header("Authorization", credential);
        }
        Request request = requestBuilder.build();
        int maxRetries = 3;
        for (int attempt = 0; attempt < maxRetries; attempt++) {
            try (Response response = clientWithTimeout.newCall(request).execute()) {
                if (response.isSuccessful()) {
                    return processResponse(response);
                } else {
                    if (response.code() >= 500 && attempt < maxRetries - 1) {
                        long sleepMillis = 1000L << attempt;
                        Thread.sleep(sleepMillis);
                    } else {
                        throw new IOException("Server returned " + response.code());
                    }
                }
            } catch (IOException | InterruptedException e) {
                if (attempt == maxRetries - 1) {
                    throw new RuntimeException("Request failed after " + maxRetries + " attempts", e);
                }
                long sleepMillis = 1000L << attempt;
                try {
                    Thread.sleep(sleepMillis);
                } catch (InterruptedException ie) {
                    Thread.currentThread().interrupt();
                    throw new RuntimeException("Interrupted during retry", ie);
                }
            }
        }
        throw new RuntimeException("Request failed after " + maxRetries + " attempts");
    }

    private List<String> processResponse(Response response) throws IOException {
        String body = response.body().string();
        return new ArrayList<>(Arrays.asList(body.split("\n")));
    }
}
