import org.apache.kafka.clients.consumer.Consumer;
import org.apache.kafka.clients.consumer.ConsumerConfig;
import org.apache.kafka.clients.consumer.ConsumerRecords;
import org.apache.kafka.clients.consumer.KafkaConsumer;
import org.apache.kafka.clients.consumer.ConsumerRecord;
import org.springframework.stereotype.Component;

import javax.annotation.PostConstruct;
import javax.annotation.PreDestroy;
import java.time.Duration;
import java.util.Collections;
import java.util.Properties;

@Component
public class KafkaConsumeOrderWorkaround {

    private String bootstrapServers;
    private String topicName;
    private String consumerGroup = "order";
    private long pollingInterval = 250L;
    private volatile boolean running;
    private Thread consumerThread;
    private Consumer<Long, String> consumer;

    public void setBootstrapServers(String bootstrapServers) {
        this.bootstrapServers = bootstrapServers;
    }

    public void setTopicName(String topicName) {
        this.topicName = topicName;
    }

    public void setConsumerGroup(String consumerGroup) {
        this.consumerGroup = consumerGroup;
    }

    public void setPollingInterval(long pollingInterval) {
        this.pollingInterval = pollingInterval;
    }

    @PostConstruct
    public void startConsuming() {
        Properties props = new Properties();
        props.put(ConsumerConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapServers);
        props.put(ConsumerConfig.GROUP_ID_CONFIG, consumerGroup);
        props.put(ConsumerConfig.KEY_DESERIALIZER_CLASS_CONFIG, "org.apache.kafka.common.serialization.LongDeserializer");
        props.put(ConsumerConfig.VALUE_DESERIALIZER_CLASS_CONFIG, "org.apache.kafka.common.serialization.StringDeserializer");
        props.put(ConsumerConfig.ENABLE_AUTO_COMMIT_CONFIG, "false");

        consumer = new KafkaConsumer<>(props);
        consumer.subscribe(Collections.singletonList(topicName));
        running = true;
        consumerThread = new Thread(this::consumeLoop);
        consumerThread.start();
    }

    private void consumeLoop() {
        while (running) {
            try {
                ConsumerRecords<Long, String> records = consumer.poll(Duration.ofMillis(pollingInterval));
                if (!records.isEmpty()) {
                    processRecords(records);
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }

    private void processRecords(ConsumerRecords<Long, String> records) {
        for (ConsumerRecord<Long, String> record : records) {
            processMessage(record);
        }
        consumer.commitAsync((offsets, exception) -> {
            if (exception != null) {
                exception.printStackTrace();
            }
        });
    }

    private void processMessage(ConsumerRecord<Long, String> record) {
        System.out.printf("Received message: key = %s, value = %s, partition = %d, offset = %d%n",
                record.key(), record.value(), record.partition(), record.offset());
    }

    @PreDestroy
    public void stopConsuming() {
        running = false;
        if (consumerThread != null) {
            consumerThread.interrupt();
        }
        if (consumer != null) {
            try {
                consumer.close();
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }
}
