import java.util.concurrent.BlockingQueue;
import java.util.concurrent.LinkedBlockingQueue;

public class NIOConnector extends Thread {
    private String name;
    private Selector selector;
    private BlockingQueue<BackendConnection> connectQueue = new LinkedBlockingQueue<>();
    private NIOProcessor[] processors;
    private int nextProcessor;
    private long connectCount;
    private final ConnectIdGenerator idGenerator = new ConnectIdGenerator();

    public NIOConnector(String name) throws IOException {
        this.name = name;
        this.selector = Selector.open();
    }

    public void setProcessors(NIOProcessor[] processors) {
        this.processors = processors;
    }

    public void postConnect(BackendConnection c) {
        try {
            connectQueue.add(c);
        } catch (Exception e) {
            e.printStackTrace();
        }
        selector.wakeup();
    }

    @Override
    public void run() {
        try {
            while (true) {
                int numKeys = selector.selectNow();
                if (numKeys > 0) {
                    Set<SelectionKey> keys = selector.selectedKeys();
                    Iterator<SelectionKey> iter = keys.iterator();
                    while (iter.hasNext()) {
                        SelectionKey key = iter.next();
                        iter.remove();
                        if (key.isConnectable()) {
                            finishConnect(key, key.attachment());
                        }
                    }
                }
                connect(selector);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private void connect(Selector selector) {
        while (true) {
            BackendConnection conn = connectQueue.poll();
            if (conn == null) {
                break;
            }
            try {
                SocketChannel channel = SocketChannel.open();
                channel.configureBlocking(false);
                SocketAddress address = conn.getSocketAddress();
                channel.connect(address);
                SelectionKey key = channel.register(selector, SelectionKey.OP_CONNECT);
                Attachment attachment = new Attachment(conn, idGenerator.next());
                key.attach(attachment);
                connectCount++;
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void finishConnect(SelectionKey key, Object att) {
        SocketChannel channel = (SocketChannel) key.channel();
        Attachment attachment = (Attachment) att;
        try {
            if (channel.finishConnect()) {
                NIOProcessor processor = nextProcessor();
                processor.registerChannel(channel, SelectionKey.OP_READ, attachment.conn);
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            clearSelectionKey(key);
        }
    }

    private void clearSelectionKey(SelectionKey key) {
        key.cancel();
        try {
            key.channel().close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private NIOProcessor nextProcessor() {
        int idx = nextProcessor++ % processors.length;
        return processors[idx];
    }

    private static class Attachment {
        private final BackendConnection conn;
        private final int id;

        public Attachment(BackendConnection conn, int id) {
            this.conn = conn;
            this.id = id;
        }
    }

    private static class ConnectIdGenerator {
        private final java.util.concurrent.atomic.AtomicInteger id = new java.util.concurrent.atomic.AtomicInteger(0);

        public int next() {
            return id.getAndIncrement();
        }
    }
}

