public class NioServerPerfTest {
    private ServerSocketChannel ch;
    private Selector selector;
    private volatile boolean running;
    private ByteBuffer buffer;
    private AtomicLong total_bytes_received = new AtomicLong(0);
    private AtomicLong total_msgs = new AtomicLong(0);
    private AtomicLong start = new AtomicLong(0);

    public ByteBuffer create(int size, boolean direct) {
        if (direct) {
            return ByteBuffer.allocateDirect(size);
        } else {
            return ByteBuffer.allocate(size);
        }
    }

    public void start(boolean direct) {
        try {
            selector = Selector.open();
            ch = ServerSocketChannel.open();
            ch.configureBlocking(false);
            ch.socket().bind(new InetSocketAddress(8080));
            ch.register(selector, SelectionKey.OP_ACCEPT);
            running = true;
            start.set(System.nanoTime());
            buffer = create(1024, direct);

            while (running) {
                selector.select();
