            int bytesRead = ch.read(buf);
            if (bytesRead == -1) return false;
            if (bytesRead > 0) {
                total_bytes_received.addAndGet(bytesRead);
                total_msgs.incrementAndGet();
            }
            return true;
        } catch (IOException e) {
            return false;
        }
    }
