import org.apache.http.client.HttpClient;
import org.apache.http.client.methods.HttpGet;
import org.apache.http.impl.client.HttpClientBuilder;
import org.apache.http.util.EntityUtils;

public class NioHttpServer {
    private final Charset charset = StandardCharsets.UTF_8;
    private final HttpClient client = HttpClientBuilder.create().build();

    public void startServer(int port) throws IOException {
        Selector selector = Selector.open();
        ServerSocketChannel serverChannel = ServerSocketChannel.open();
        serverChannel.configureBlocking(false);
        serverChannel.bind(new InetSocketAddress(port));
        serverChannel.register(selector, SelectionKey.OP_ACCEPT);

        while (true) {
            selector.select();
            Set<SelectionKey> selectedKeys = selector.selectedKeys();
            Iterator<SelectionKey> it = selectedKeys.iterator();
            while (it.hasNext()) {
                SelectionKey key = it.next();
                it.remove();
                if (!key.isValid()) continue;
                if (key.isAcceptable()) handleAccept(key, selector);
                else if (key.isReadable()) handleRead(key, selector);
                else if (key.isWritable()) handleWrite(key, selector);
            }
        }
    }

    private void handleAccept(SelectionKey key, Selector selector) throws IOException {
        ServerSocketChannel serverChannel = (ServerSocketChannel) key.channel();
        SocketChannel clientChannel = serverChannel.accept();
        clientChannel.configureBlocking(false);
        clientChannel.register(selector, SelectionKey.OP_READ);
    }

    private void handleRead(SelectionKey key, Selector selector) throws IOException {
        SocketChannel channel = (SocketChannel) key.channel();
        ByteBuffer buffer = ByteBuffer.allocate(1024);
        StringBuilder request = new StringBuilder();
        while (channel.read(buffer) > 0) {
            buffer.flip();
            request.append(charset.decode(buffer));
            buffer.clear();
        }
        if (request.length() == 0) return;
        String response = "HTTP/1.1 200 OK\r\nContent-Type: text/html; charset=UTF-8\r\nContent-Length: " + executeResponse().length() + "\r\n\r\n" + executeResponse();
        key.attach(ByteBuffer.wrap(response.getBytes(charset)));
        key.interestOps(SelectionKey.OP_WRITE);
    }

    private void handleWrite(SelectionKey key, Selector selector) throws IOException {
        SocketChannel channel = (SocketChannel) key.channel();
        ByteBuffer buffer = (ByteBuffer) key.attachment();
        if (buffer == null || !buffer.hasRemaining()) {
            channel.close();
            return;
        }
        channel.write(buffer);
        if (!buffer.hasRemaining()) {
            key.interestOps(0);
            channel.close();
        }
    }

    private String executeResponse() {
        try {
            HttpResponse httpResponse = client.execute(new HttpGet("http://www.baidu.com/"));
            HttpEntity entity = httpResponse.getEntity();
            return entity != null ? EntityUtils.toString(entity, charset) : "No content";
        } catch (Exception e) {
            return "Error";
        }
    }
}
