    private Selector selector;

    public void start(int port) {
        try {
            serverSocketChannel = ServerSocketChannel.open();
            serverSocketChannel.bind(new InetSocketAddress(port));
            serverSocketChannel.configureBlocking(false);
            selector = Selector.open();
            serverSocketChannel.register(selector, SelectionKey.OP_ACCEPT);
            while (true) {
                int readyChannels = selector.select();
                if (readyChannels == 0) continue;
                Iterator<SelectionKey> iterator = selector.selectedKeys().iterator();
                while (iterator.hasNext()) {
                    SelectionKey key = iterator.next();
                    iterator.remove();
                    if (key.isAcceptable()) {
                        handleAccept(key);
                    } else if (key.isReadable()) {
                        handleRead(key);
                    } else if (key.isWritable()) {
                        handleWrite(key);
                    }
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                if (selector != null) selector.close();
                if (serverSocketChannel != null) serverSocketChannel.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void handleAccept(SelectionKey key) throws IOException {
        ServerSocketChannel serverChannel = (ServerSocketChannel) key.channel();
        SocketChannel clientChannel = serverChannel.accept();
        clientChannel.configureBlocking(false);
        SelectionKey clientKey = clientChannel.register(selector, SelectionKey.OP_READ);
        clientKey.attach(ByteBuffer.allocate(1024));
    }

    private void handleRead(SelectionKey key) throws IOException {
        SocketChannel channel = (SocketChannel) key.channel();
        ByteBuffer buffer = (ByteBuffer) key.attachment();
        if (buffer == null) {
            buffer = ByteBuffer.allocate(1024);
            key.attach(buffer);
        }
        int bytesRead;
        try {
            bytesRead = channel.read(buffer);
        } catch (IOException e) {
            key.cancel();
            channel.close();
            return;
        }
        if (bytesRead == -1) {
            key.cancel();
            channel.close();
            return;
        }
        buffer.flip();
        int endIndex = -1;
        for (int i = 0; i < buffer.remaining() - 3; i++) {
            if (buffer.get(i) == '\r' && buffer.get(i + 1) == '\n' && buffer.get(i + 2) == '\r' && buffer.get(i + 3) == '\n') {
                endIndex = i + 4;
                break;
            }
        }
        if (endIndex != -1) {
            byte[] requestData = new byte[endIndex];
            buffer.get(requestData);
            String requestLine = new String(requestData).split("\r\n")[0];
            System.out.println("Received: " + requestLine);
            String response = "HTTP/1.1 200 OK\r\nContent-Length: 13\r\n\r\nHello, World!";
            ByteBuffer responseBuffer = ByteBuffer.wrap(response.getBytes());
            key.attach(responseBuffer);
            key.interestOps(SelectionKey.OP_WRITE);
        }
        buffer.compact();
    }

    private void handleWrite(SelectionKey key) throws IOException {
        SocketChannel channel = (SocketChannel) key.channel();
        ByteBuffer buffer = (ByteBuffer) key.attachment();
        if (buffer == null) return;
        try {
            channel.write(buffer);
        } catch (IOException e) {
            key.cancel();
            channel.close();
            return;
        }
        if (!buffer.hasRemaining()) {
            key.cancel();
            channel.close();
        }
    }
